INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (660, 143, 'Paclitaxel', '<b>Paclitaxel</b>', '<b>Paclitaxel</b>250 mg/m<sup>2</sup> IV over 24 hours on day 1<br>Repeat cycle every 21 days. G-CSF support is recommended.', 'Moderate', '$Ajani JA, et al. Semin Oncol 1995;22 (Suppl 6):35-40.$', '<b>Hepatic(Liver)</b><ul><li>Paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                                                     If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m<sup>2</sup>                                                                                       If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (661, 144, 'DCF', '<b>DCF</b>', '<b>Docetaxel</b> 75mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1-3 hours, day 1<br> <b>Fluorouracil</b> 750 mg/m<sup>2</sup>/d CIVI, days 1-5<br> Repeat every 21 days', 'High', 'Van Custem E, Moiseyenko VM, Tjulandin S, et al. Phase III study of docetaxel and cisplatin plus florouracil compared with cisplatin and flouorouracil as first-line therapy for advanced gastric cancer', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use <br>                                             If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                              </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction</li><li>Fluorouracil:<br>No dose reduction</li></ul>', 'CDDP<br>Taxotere<br>5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (662, 144, 'Docetaxel/Cisplatin', '<b>Docetaxel/Cisplatin</b>', '<b>Docetaxel</b> 85 mg/m<sup>2</sup> IV over 1 hour, day 1,<br> <b>FOLLOWED BY</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1 hour, day 1<br> Repeat every 21 days for a maximum of 8 cycles', 'High', 'Roth AD, Maibach R, Martinelli G, et al. Docetaxel (Taxotere)-cisplatin (TC): an effective drug combination in gastric carcinoma. $Ann Oncol. 2000;11(3):301-306$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use <br>                                             If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                              </li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction</li></ul>', 'CDDP<br>Taxotere');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (663, 144, 'ECF', '<b>ECF</b>', '<b>Epirubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 60 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 200 mg/m<sup>2</sup>/d CIVI, for up to 6 months<br> Repeat every 21 days for a maximum of 8 cycles', 'High', 'Waters JS, Norman A, Cunningham D, et al. Long-term survival afer epirubicin, cisplatin and fluorouracil for gastric cancer: results of a randomized trial. $Br J Cancer. 1999;80(1-2):269-272.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li></ul>', 'CDDP<br>Ellence, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (664, 144, 'ELF', '<b>ELF</b>', '<b>Leucovorin calcium</b> 300 mg/m<sup>2</sup>/d IV over 10 minutes, <b>FOLLOWED BY</b><br> <b>Etoposide</b> 120 mg/m<sup>2</sup>/d IV over 50 minutes,<br> <b>THEN</b><br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV  bolus<br> Give all agents on days 1-3; repeat every 22 days', 'Low', 'Vanhoefer U, Rougier P, Wilke H, et al. Final results of a randomized phase III trial of sequential high-dose methotrexate, fluorouracil, and doxorubicin versus etoposide, leucovorin, and fluorouracil', '<b>Hepatic(Liver)</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Fluorouracil:<br>No dose reduction</li></ul>', 'VP-16<br>5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (665, 144, '5-FU/LV and Radiation', '<b>5-FU/LV and Radiation</b>', '<b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days,<br> <b>FOLLOWED BY</b><br> <b>RT</b> 4,500 cGy (180cGy/d), 5 days a week for 5 weeks<br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup>/d IV, on the first 4 and last 3 days of RT<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d, on the first 4 and last 3 days of RT,<br> <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days<br>repeat 28 days X 2 cycles', 'Low', 'Macdonald JS, Smalley SR, Benedetti J, et al. Chemoradiotherapy afer surgery compared with surgery alone for adenocarcinoma of the stomach or gastroesophageal junction. $N Engl J Med. 2001;345(10):725', '<b>Hepatic(Liver)</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Fluorouracil:<br>No dose reduction</li></ul>', '5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (666, 144, 'Trastuzumab + Cisplatin + Capecitabine or Fluorouracil', '<b>Trastuzumab + Cisplatin + Capecitabine or Fluorouracil</b>', '<b>Trastuzumab</b> 8 mg/kg IV, day 1 of the first cycle <b>THEN</b>  6 mg/kg IV, day 1<br><br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1. <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14 <br> Repeat  every 21 days<br> <b>OR</b><br> <b>Fluorouracil</b> 800 mg/m<sup>2</sup>/d CIVI for 5 days of each 3-week cycle<br> Repeat for 6 cycles', 'High day 1, low for the remaining treatment days', 'Bang YJ, Van Cutsem E, Feyereislava A, et al. Trastuzumab in combination with chemotherapy versus chemotherapy alone for treatment of HER2-positive advanced gastric or gastro-oesophageal junction canc', '<b>Hepatic(Liver)</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 30 mL/min, do not use</li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction</li></ul>', 'Xeloda<br>CDDP<br>5-FU<br>Herceptin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (667, 144, 'Capecitabine', '<b>Capecitabine</b>', '<b>Capecitabine</b> 1,250 mg/m<sup>2</sup> days 1-14,  <br> Repeat every 21 days for  6 cycles', 'Low', 'Hong YS, Song SY, Lee SI, et al. A phase II trial of capecitabine in previously untreated patients with advanced and/or metastatic gastric cancer. $Ann Oncol. 2004;15(9):1344-1347.$', '<b>Hepatic(Liver)</b><ul><li>Capecitabine:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 30 mL/min, do not use</li></ul>', 'Xeloda');
