INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (670, 147, 'Bevacizumab + Erlotinib', '<b>Bevacizumab + Erlotinib</b>', '<b>Bevacizumab</b>10 mg/kg IV on days 1 and 14<br><b>Erlotinib</b>150 mg PO daily<br>Repeat cycle every 28 days.', 'Low', '$Thomas MB, et al. J Clin Oncol 2009;27:843-850.$', '<b>Hepatic(Liver)</b><ul><li>Erlotinib:<br>If AST > 3 upper normal limit, 50 mg <br>                                                         If direct bilirubin 1-7 mg/dL, 75 mg</li></ul><b>Renal(Kidney)</b><ul><li>Erlotinib:<br>No dose reduction</li></ul>', 'Avastin<br>Tarceva');
