INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (672, 147, 'Doxorubicin', '<b>Doxorubicin</b>', '<b>Doxorubicin</b>20-30 mg/m<sup>2</sup> IV weekly<br>Repeat cycle every week.', 'Moderate', '$Venook AP. J Clin Oncol 1994;12:1323-1334.$', '<b>Hepatic(Liver)</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Doxorubicin:<br>No dose reduction</li></ul>', 'Adriamycin, various');
