INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (685, 148, 'FOLFIRINOX', '<b>FOLFIRINOX</b>', '<b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV over 2 hours <b>FOLLOWED BY Leucovorin calcium</b> 400 mg/m<sup>2</sup> IV over 2 hours with the addition, after 30 minutes <b>Irinotecan</b> 180 mg/m<sup>2</sup> IV over 90 minutes; <b>IMMEDIATELY FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus, <b>THEN</b><br> <b>Fluorouracil</b> 2,400 mg/m<sup>2</sup> IV over 46 hours<br> Repeat cycle every 2 weeks', 'Moderate', 'Conroy T, Desseigne F, Ychou M, et al. FOLFIRINOX versus gemcitabine for metastatic pancreatic cancer. $N Engl J Med. 2011;364(19):1817-1825.$', '<b>Hepatic(Liver)</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose <br>                              If bilurubin > 2 mg/dL, may not use</li></ul><b>Renal(Kidney)</b><ul><li>Fluorouracil:<br>No dose reduction</li><li>Irinotecan:<br>No dose reduction</li></ul>', '5-FU<br>Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (686, 148, 'Gemcitabine/Capecitabine', '<b>Gemcitabine/Capecitabine</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes, days 1, 8<br> <b>Capecitabine</b> 650 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days.', 'Low', 'Herrmann R, Bodoky G, Ruhstaller T, et al. Gemcitabine plus capecitabine compared with gemcitabine alone in advanced pancreatic cancer: a randomized, multicenter, phase III trial of the Swiss Group fo', '<b>Hepatic(Liver)</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction</li></ul>', 'Xeloda<br>Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (687, 148, 'Gemcitabine/Erlotinib', '<b>Gemcitabine/Erlotinib</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes once weekly for 7 weeks,<br> <b>FOLLOWED BY</b> 1 week of rest<br> <b>Erlotinib</b> 100 <b>OR</b> 150 mg/d PO<br> Repeat subsequent cycles once weekly for 3 consecutive weeks out of every 4 weeks', 'Low', 'Moore MJ, Goldstein D, Hamm J, et al. Erlotinib plus gemcitabine compared with gemcitabine alone in patients with advanced pancreatic cancer: a phase III trial of the National Cancer institute of Cana', '<b>Hepatic(Liver)</b><ul><li>Erlotinib:<br>If AST > 3 upper normal limit, 50 mg <br>                                                         If direct bilirubin 1-7 mg/dL, 75 mg</li><li>Gemcitabine:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Erlotinib:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction</li></ul>', 'Tarceva<br>Gemzar, various');
