INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (705, 142, 'Capecitabine', '<b>Capecitabine</b>', '<b>Capecitabine</b> 1,000- 1,250 mg/m<sup>2</sup>/d PO bid, days 1-14, <b>FOLLOWED BY</b> 7 days rest<br> Repeat every 21 days', 'Low', 'Van Cutsem E, Twelves C, Cassidy J, et al. Oral capecitabine compared with intravenous fluorouracil plus leucovorin in patients with metastatic colorectal cancer: results of a large Phase III Study. $', '<b>Hepatic(Liver)</b><ul><li>Capecitabine:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 30 mL/min, do not use</li></ul>', 'Xeloda');
