INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (708, 142, 'Irinotecan', '<b>Irinotecan</b>', '<b>Irinotecan</b> 125 mg/m<sup>2</sup>/d IV over 90 minutes, days 1,8, 15,22 <b>FOLLOWED BY</b> 2 weeks rest<br> Repeat every 6 weeks<br> <b>OR</b><br> <b>Irinotecan</b> 350 mg/m<sup>2</sup> IV over 90 minutes<br> Repeat every 21 days', 'Moderate', 'Rougier P, Bugat R, Douillard JY, et al. Phase II study of irinotecan in the treatment of advanced colorectal cancer in chemotherapy-naïve patients and patients pretreated with fluorouracil-based chem', '<b>Hepatic(Liver)</b><ul><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose <br>                              If bilurubin > 2 mg/dL, may not use</li></ul><b>Renal(Kidney)</b><ul><li>Irinotecan:<br>No dose reduction</li></ul>', '');
