INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (710, 141, 'Fluorouracil/Mitomycin C', '<b>Fluorouracil/Mitomycin C</b>', '<b>5-Fluorouracil</b> 1,000 mg/m<sup>2</sup>/day CIVI,  days 1-4 and 29-32<br> <b>Mitomycin C</b> 10 mg/m<sup>2</sup> IV  days 1 and 29 and radiation concurrently.', 'Low', 'Ajani JA, Winter KA, et al. Fluorouracil, mitomycin, and radiotherapy vs fluorouracil, cisplatin, and radiotherapy for carcinoma of the anal canal: a randomized controlled trial. $JAMA. 2008;299(16):1', '<b>Hepatic(Liver)</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Fluorouracil:<br>No dose reduction</li></ul>', '5-FU');
