INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (714, 149, 'Carbo-Tax', '<b>Carbo-Tax</b>', '<b>Paclitaxel</b> 225mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Carboplatin</b> dose targeted by AUC of 6 mg/mL/min, day 1<br> Repeat every 21 days', 'Moderate', 'Schiller JH, Harrington D, Belani CP, et al. Comparison of four chemotherapy regimens for advanced non-small-cell lung cancer. $N Engl J Med. 2002;346(2):92-98.$', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                                                     If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m<sup>2</sup>                                       