INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (715, 149, 'Cisplatin/Pemetrexed', '<b>Cisplatin/Pemetrexed</b>', '<b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Pemetrexed</b> 500 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 6 cycles', 'High', 'Scagliotti GV, Parikh P, von Pawel J, et al. Phase III study comparing cisplatin plus gemcitabine with cisplatin plus pemetrexed in chemotherapy-naïve patients with advanced-stage non-small-cell lung ', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Pemetrexed:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Pemetrexed:<br>If CrCl > 45 mL/min, no dose reduction <br>                                                                           If CrCl < 45 mL/min, do not use</li></ul>', 'CDDP<br>Alimta');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (716, 149, 'Cisplatin/Vinorelbine/Cetuximab', '<b>Cisplatin/Vinorelbine/Cetuximab</b>', '<b>Cetuximab</b> 400 mg/m<sup>2</sup> IV initial dose, <b>THEN</b><br> 250 mg/m<sup>2</sup>/wk<br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1<br> <b>Vinorelbine</b> 25 mg/m<sup>2</sup> IV, days 1, 8<br> Repeat every 21 days', 'High', 'Pirker R, Pereira JR, Szczesna A, et al. Cetuximab plus chemotherapy in patients with advanced non-small-cell lung cancer (FLEX): an open-label randomised phase III trial. $Lancet. 2009;373(9674):1525', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Vinorelbine:<br>If bilirubin 2-3 mg/dL, reduce dose by 50% <br>                                              If bilirubin 3.1-5 mg/dL, reduce dose by 75% <br>                                           If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Vinorelbine:<br>No dose reduction</li></ul>', 'Erbitux<br>CDDP<br>Navelbine');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (717, 149, 'Docetaxel/Cisplatin', '<b>Docetaxel/Cisplatin</b>', '<b>Docetaxel</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days', 'High', 'Schiller JH, Harrington D, Belani CP, et al. Comparison of four chemotherapy regimens for advanced non-small-cell lung cancer. $N Engl J Med. 2002;346(2):92-98.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use <br>                                             If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                              </li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction</li></ul>', 'CDDP<br>Taxotere');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (718, 149, 'Gemcitabine/Cisplatin', '<b>Gemcitabine/Cisplatin</b>', '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup> /d IV,days 1,8,15<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> Repeat every 28 days<br> <b>OR</b><br> <b>Gemcitabine</b> 1,250 mg/m<sup>2</sup>/d IV over 30 minutes, days 1, 8<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> IV over 60 minutes, day 1<br> Repeat every 21 days                                         ', 'High', 'Schiller JH, Harrington D, Belani CP, et al. Comparison of four chemotherapy regimens for advanced non-small-cell lung cancer. $N Engl J Med. 2002;346(2):92-98.$<b> Cardenal F, Lopez-Cabrerizo MP, Ant', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction</li></ul>', 'CDDP<br>Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (719, 149, 'Gemcitabine/Docetaxel', '<b>Gemcitabine/Docetaxel</b>', '<b>Gemcitabine</b> 1,100 mg/m<sup>2</sup>/d IV over 30 minutes, days 1, 8<br> <b>Docetaxel</b> 100 mg/m<sup>2</sup> IV over 1 hour, day 8<br>  Repeat every 21 days.', 'Low', 'Georgoulias V, Papadakis E, Alexopoulos A, et al. Platinum-based and non-platinum-based chemotherapy in advanced non-small-cell lung cancer: a randomized multicentre trial. $Lancet. 2001;357(9267):147', '<b>Hepatic(Liver)</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use <br>                                             If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                              </li><li>Gemcitabine:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Docetaxel:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction</li></ul>', 'Taxotere<br>Gemzar, various');
