INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (726, 149, 'Docetaxel', '<b>Docetaxel</b>', '<b>Docetaxel</b> 75mg/m<sup>2</sup> IV over 1hour.<br> Repeat every 21 days', 'Low', 'Shepherd FA, Dancey J, Ramlau R, et al. Prospective randomized trial of docetaxel versus best supportive care in patients with non-small-cell lung cancer previously treated with platinum-based chemoth', '<b>Hepatic(Liver)</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use <br>                                             If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                              </li></ul><b>Renal(Kidney)</b><ul><li>Docetaxel:<br>No dose reduction</li></ul>', 'Taxotere');
