INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (730, 149, 'Vinorelbine', '<b>Vinorelbine</b>', '<b>Vinorelbine</b> 30 mg/m<sup>2</sup> IV over 20 minutes, day 1,8,15,22.<br> Repeat every 7 days .', 'Low', 'Le Chevalier T, Brisgand D, Douillard JY, et al. Randomized study of vinorelbine and cisplatin versus vindesin and cisplatin versus vinorelbine alone in advanced non-small-cell lung cancer: results of', '<b>Hepatic(Liver)</b><ul><li>Vinorelbine:<br>If bilirubin 2-3 mg/dL, reduce dose by 50% <br>                                              If bilirubin 3.1-5 mg/dL, reduce dose by 75% <br>                                           If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Vinorelbine:<br>No dose reduction</li></ul>', 'Navelbine');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (731, 150, 'Carboplatin/Irinotecan', '<b>Carboplatin/Irinotecan</b>', '<b>Carboplatin</b> AUC of 5 IV, day 1<br> <b>Irinotecan</b> 175mg/m<sup>2</sup> IV, day 1<br> Repeat every 21days', 'Moderate', 'Hermes A, Bergman B, Bremnes R, et al           Irinotecan plus carboplatin in extensive versus oral etoposide plus carboplatin in extensive small-cell lung cancer: a randomized phase III trial. $J Cl', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose <br>                              If bilurubin > 2 mg/dL, may not use</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Irinotecan:<br>No dose reduction</li></ul>', 'Paraplatin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (732, 150, 'Cisplatin + CPT-11', '<b>Cisplatin + CPT-11</b>', '<b>Cisplatin</b> 60mg/m<sup>2</sup>, day 1<br> <b>Irinotecan</b> 60mg/m<sup>2</sup>/d, days 1, 8, 15<br> Repeat every 28days', 'High', 'Noda K, Nishiwaki Y, Kawahara M, et al.                     Irinotecan plus cisplatin compared with etoposide plus cisplatin for extensive small-cell lung cancer. $N Engl J Med. 2002;346(2):85-91.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose <br>                              If bilurubin > 2 mg/dL, may not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Irinotecan:<br>No dose reduction</li></ul>', 'CDDP');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (733, 150, 'CAV', '<b>CAV</b>', '<b>Cyclophosphamide</b>  1,000 mg/m<sup>2</sup>IV on day 1.  <br>                                              <b>Doxorubicin</b> 40 mg/m<sup>2</sup>IV on day 1.<br>                                                                <b>Vincristine</b> 1 mg/m<sup>2</sup>IV on day 1. (maximum, 2 mg) <br>Repeat cycle every 21 days.', 'High', '$Roth  BJ, et al. J Clin Oncol 1992;10:282-291.$', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Adriamycin, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (734, 150, 'Carboplatin + Paclitaxel', '<b>Carboplatin + Paclitaxel</b>', '<b>Carboplatin</b>AUC of 2, IV on days1,8, and 15.<br><b>Paclitaxel</b> 80 mg/m<sup>2</sup> IV on days 1,8, and 15. Repeat cycle every 28 days.', 'Moderate', '$Neubauer M, et al. J Clin Oncol2004;22:1872-1877$', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                                                     If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m<sup>2</sup>                                                                                       If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Paraplatin<br>Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (735, 150, 'EC', '<b>EC</b>', '<b>Etoposide</b> 100mg/m<sup>2</sup>/d IV, days 1-3<br><b>Carboplatin</b> 450mg/m<sup>2</sup> IV, day 1<br> Repeat every 28 days for 6 cycles', 'Moderate', 'Viren M, Liippo K, Ojala a, et al. Carboplatin and etoposide in extensive small-cell lung cancer. $Acta Oncal. 1994;33(8):921-924.$', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'Paraplatin<br>VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (736, 150, 'EP', '<b>EP</b>', '<b>Etoposide</b> 80mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cisplatin</b> 80mg/m<sup>2</sup> IV, day 1 (Standard dose)<br> Repeat every 21 days <b>OR</b><br> <b>Etoposide</b> 80mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 27mg/m<sup>2</sup> IV, days 1-5(high dose)<br> Repeat every 21 days', 'High', 'Ihde DC, Mulshine JL, Kramer BS, et al.  Prospective randomized comparision of high-dose and standard-dose etoposide and cisplatin chemotherapy in patients with extensive-stage small-cell lung cancer.', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'CDDP<br>VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (737, 150, 'Etoposide', '<b>Etoposide</b>', '<b>Etoposide</b> 160 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 28 days<br> <b>OR</b><br> <b>Etoposide</b> 50 mg PO bid for 14 days<br> Repeat every 21 days', 'Low', 'Johnson DH. Recent developments in chemotherapy treatment of small-cell lung cancer. $Semin Oncol. 1993;20(4):315-325.$', '<b>Hepatic(Liver)</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (738, 150, 'Paclitaxel', '<b>Paclitaxel</b>', '<b>Paclitaxel</b> 250 mg/m<sup>2</sup> IV over 24 hours<br> Repeat every 21 days', 'Low', 'Ettinger DS, Finkelstein DM, Sarma RP, Johnson DH. Phase II study of paclitaxel in patients with extensive-disease small-cell lung cancer: an Eastern Cooperative Oncology Group study. $J Clin Oncol. 1', '<b>Hepatic(Liver)</b><ul><li>Paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                                                     If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m<sup>2</sup>                                                                                       If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Taxol');
