INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (741, 151, 'CAP', '<b>CAP</b>', '<b>Cyclophosphamide</b> 500 mg/m<sub>2</sub> IV on day 1<br><b>Doxorubicin</b> 500 mg/m<sub>2</sub> IV on day 1<br><b>Cisplatin</b> 500 mg/m<sub>2</sub> IV on day 1<br>Repeat cycle every 21 days.', NULL, 'Loehrer PJ, et al.$J Clin Oncol 1994;12:1164-1168.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li></ul>', 'CDDP<br>Cytoxan<br>Adriamycin, various');
