INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (743, 151, 'ADOC', '<b>ADOC</b>', '<b>Cisplatin</b> 50 mg/m<sub>2</sub> IV on day 1<br><b>Doxorubicin</b> 40 mg/m<sub>2</sub> IV on day 1<br><b>Vincristine</b> 0.6 mg/m<sub>2</sub> IV on day 3<br><b>Cyclophosphamide</b> 700 mg/m<sub>2</sub> IV on day 4<br>Repeat cycle every 28 days.', NULL, 'Fornasiero A, et al.$Cancer 1991;68:30-33.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'CDDP<br>Cytoxan<br>Adriamycin, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (744, 151, 'VIP', '<b>VIP</b>', '<b>Etoposide(VP-I6)</b> 75 mg/m<sub>2</sub> IV on days 1-4<br><b>Ifosfamide</b> 1200 mg/m<sub>2</sub> IV on days 1-4<br><b>Cisplatin</b> 20 mg/m<sub>2</sub> IV on days 1-4<br><b>Mesna</b> 240 mg/m<sub>2</sub> IV before first ifosfamide dose, then 4 and 8 hours later on days 1-4<br>Repeat cycle every 21 days for a total of 4 cycles.G-CSF support at 5 micro grams/kg/day on days 5-15', NULL, 'Loehrer PJ, et al.$Cancer 2001;91:2010-2015.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'CDDP<br>VP-16<br>Ifex<br>Mesnex, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (745, 151, 'Gemcitabine + Topotecan', '<b>Gemcitabine + Topotecan</b>', '<b>Gemcitabine</b>1000 mg/m<sub>2</sub> IV on days 1,8, and 15.<br><b>Topotecan</b>0.75-1.5 mg/m<sub>2</sub> IV on days 1,8, and 15.<br> Repeat cycle every 21 days', NULL, 'William WN, et al.$Am J Clin Oncol 2009;32:15-19.$', '<b>Hepatic(Liver)</b><ul><li>Gemcitabine:<br>No dose reduction</li><li>Topotecan:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Gemcitabine:<br>No dose reduction</li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                         If CrCl 20-39 mL/min, reduce to 0.75 mg/m<sup>2</sup> </li></ul>', 'Gemzar, various<br>Hycamtin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (746, 151, 'Carboplatin + Paclitaxel', '<b>Carboplatin + Paclitaxel</b>', '<b>Carboplatin</b> AUC of 6, IV on day 1 <br><b>Paclitaxel</b>225 mg/m<sub>2</sub> IV on day 1<br> Repeat cycle every 21 days', NULL, 'Lemma GL, et al.$ J Clin Oncol 2011;29:2060-2065.$', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                                                     If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m<sup>2</sup>                                                                                       If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Paraplatin<br>Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (747, 152, 'AC', '<b>AC</b>', '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles', 'High', 'Fisher B,Brown AM,Dimitor NV,et al.Two months of doxorubicin-cyclophosphamide with and without interval reinduction therapy compared with 6 months of cyclophosphamide,methotrexade,and fluorouracil in ', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li></ul>', 'Cytoxan<br>Adriamycin, various');
