INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (756, 152, 'CAF(oral)', '<b>CAF(oral)</b>', '<b>Cyclophosphamide</b> 100mg/m<sup>2</sup> PO, days 1-14<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV ,days 1,8<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup> IV, days 1,8<br> Repeat every 28 days for 6 cycles      ', 'Moderate-High', 'Hutchins LF, Green SJ, Favolin PM, et al. Randomized controlled trial of cyclophosphamide, methotrexate, and fluorouracil versus cyclophosphamide, doxorubicin, and fluorouracil with and without tamoxi', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Fluorouracil:<br>No dose reduction</li></ul>', 'Cytoxan<br>Adriamycin, various<br>5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (757, 152, 'FEC', '<b>FEC</b>', '<b>Cyclophosphamide</b> 75 mg/m<sup>2</sup>/d PO, days 1-14<br> <b> Epirubicin</b> 60 mg/m<sup>2</sup>/d IV, days 1,8<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV, days 1,8<br> Repeat every 28 days for 6 cycles<br> <b>OR</b><br> <b>Cyclophosphamide</b> 500 mg/m<sup>2</sup> IV, day 1<br> <b>Epirubicin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for 4 cycles', 'High', 'Levine MN, Bramwell VH, Pritchard KI, et al. Randomized tiral of intensive cyclophosphamide, epirubicin, and fluorouracil chemotherapy compared with cyclophosphamide, methotrexate, and fluorouracil in', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Fluorouracil:<br>No dose reduction</li></ul>', 'Cytoxan<br>Ellence, various<br>5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (758, 152, 'CMF', '<b>CMF</b>', '<b>Cyclophosphamide </b>100 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Methotrexate</b> 40 mg/m<sup>2</sup>/d IV, days 1,8<br> <b>Fluorouracil</b> 600 mg/m<sup>2</sup>/d IV, days 1, 8<br> Repeat every 28 days for up to 6 cycles<br> <b>OR</b><br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup> IV, day 1<br> <b>Methotrexate</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 600 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 12 courses', 'Moderate for IV, Moderate-High for PO', 'Amadori D, Nanni O, Marangolo M, et al. Disease-free survival advantage of adjuvant cyclophosphamide, methotrexate, and fluorouracil in patients with node-negative, rapdly proliferating breast cancer:', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction <br>                                                    If bilirubin > 5 mg/dL, do not use<br>                                                              No dose reduction for infusional administration</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Fluorouracil:<br>No dose reduction</li></ul>', 'Cytoxan<br>5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (759, 152, 'Gemcitabine/Paclitaxel', '<b>Gemcitabine/Paclitaxel</b>', '<b>Gemcitabine 1,250 mg/m<sup>2</sup> IV over 30-60 minutes, days 1, 8<br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> IV over 3 hours before gemcitabine, day 1<br> Repeat every 21 days', 'Low', 'Albain KS, Nag SM, Calderillo-Ruiz G, et al. Gemcitabine plus paclitaxel versus paclitaxel monotherapy in patients with metastatic breast cancer and prior anthracycline treatment. $J Clin Oncol. 2008;', '<b>Hepatic(Liver)</b><ul><li>Gemcitabine:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                                                     If transaminase < 10 upper normal limit and bili