INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (761, 152, 'Ixabepilone/Capecitabine', '<b>Ixabepilone/Capecitabine</b>', '<b>Ixabepilone</b> 40 mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days for up to 8 cycles', 'Low', 'Thomas ES, Gomez HL, Li RK, et al. Ixabepilone plus capecitabine for metastatic breast cancer progressing after anthracycline and taxane treatment. $J Clin Oncol. 2007;25(33):5210-5217.$', '<b>Hepatic(Liver)</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Ixabepilone:<br>Monotherapy:                                                                                                   If AST and ALT < 2.5 upper normal limit, and bilirubin < 1 upper normal limit, use 40 mg/m<sup>2</sup> <br>                                                                       If AST or ALT < 10 upper normal limit, and bilirubin < 1 upper normal limit, use 32 mg/m <sup>2</sup> <br>                                                                                      If AST and ALT < 10 upper normal limit, and bilirubin 1.5-3 upper normal limit, use 20-30 mg/m <sup>2</sup> <br>                                                                  If AST or ALT > 10 upper normal limit, or bilirubin > 3 upper normal limit, do not use <br>                                                                                                                                                                                   In combination with capecitabine:                                                                If AST and ALT < 2.5 upper normal limit, and bilirubin < 1 upper normal limit, use 40 mg/m <sup>2</sup> <br>                                                                        If AST or ALT > 2.5 upper normal limit, or bilirubin > 1 upper normal limit, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 30 mL/min, do not use</li><li>Ixabepilone:<br>If CrCl > 30 mL/min, no dose reduction</li></ul>', 'Xeloda<br>Ixempra');
