INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (820, 162, 'VIP', '<b>VIP</b>', '<b>Vinblastine</b> 0.11 mg/kg/d IV, days 1, 2<br> <b>OR</b><br> <b>Etoposide</b> 75 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>AND</b><br> <b>Infosfamide</b> 1,2000 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 20mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Mesna</b> 400 mg IV 15 minutes prior to ifosfamide, <b>THEN</b> 1,200 mg/d CIVI, days 1-5<br> Repeat every 21 days for 4 cycles', 'High', 'Loehrer PJ, Lauer R, Roth BJ, et al. Salvage therapy in recurrent germ-cell cancer: ifosfamide and cisplatin plus either vinblastine or etoposide. $Ann Intern Med. 1988;109(7):540-546.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Mesna:<br>No dose reduction</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vinblastine:<br>No dose reduction</li></ul>', 'CDDP<br>VP-16<br>Mesnex, various<br>Velban');
