INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (839, 163, 'MAID', '<b>MAID</b>', '<b>Mesna</b> 2,500 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Ifosfamide</b> 2,500 mg/m<sup>2</sup>/d CIVI, days 1-3<br> <b>Dacarbazine</b> 250 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days', 'High', 'Antman K, Crowley J, Balcerzak SP, et al. An intergroup phase III randomized study of doxorubicin and dacarbazine with or without ifosfamide and mesna in advanced soft tissue and bone sarcomas. $J Cli', '<b>Hepatic(Liver)</b><ul><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%  <br>                                                                    If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li></ul>', 'DTIC<br>Adriamycin, various<br>Ifex<br>Mesnex, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (840, 163, 'Methotrexate/Cisplatin/Doxorubicin Ifosfamide', '<b>Methotrexate/Cisplatin/Doxorubicin Ifosfamide</b>', '<b>Methotrexate</b> 12,000 mg/m<sup>2</sup> IV over 4 hours day 1<br> <b>WITH Leucovorin calcium</b> 8 mg/m<sup>2</sup> IV every 6 hours for 11 doses beginning 24 hours from the start of the Methotrexate infusion, days 1, 42 (preoperatively); days 133, 196, 238 (postoperatively)<br> <b>Cisplatin</b> 120 mg/m<sup>2</sup> CIVI over 48 hours, days 7-8, 49-50 (preoperatively); days 140-141, 203-204, 245-246 (postoperatively). <br> <b>Doxorubicin</b> 75 mg/m<sup>2</sup> CIVI over 24 hours, days 7, 49 (preoperatively); days 98, 161 (postoperatively). Doxorubicin was increased to 90 mg/m<sup>2</sup> IV over 24 hours for postoperative treatment courses<br> <b>Ifosfamide</b> 3,000 mg/m<sup>2</sup>/d CIVI for 5 days with equimolar doses of mesna, days 28-32, 70-74 (preooperatively); days 119-123, 182-186, 224-228 (postoperatively)', 'High', 'Ferrrari S, Smeland S, Mercuri M, et al. Neoadjuvant chemotherapy with high-dose ifosfamide, high-dose methotrexate, cisplatin, and doxorubicin for patients with localized osteosarcoma of the extremit', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Methotrexate:<br>If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br> If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Methotrexate:<br>If CrCl 30-60 mL/min, reduce dose by 50%    <br>                                                                  If CrCl < 30 mL/min, do not use</li></ul>', 'CDDP<br>Adriamycin, various<br>Ifex');
