INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (846, 165, 'Carbo-Tax', '<b>Carbo-Tax</b>', '<b>Carboplatin</b> AUC of 6 mg/ml/min IV over 30 minutes.<br><b>FOLLOWED  BY</b><br><b>Paclitaxel</b> 200mg/m<sup>2</sup>  IV over 3 hours, day 1<br><b> Filgrastim </b> 300 mcg/d SQ, days 5-12<br><b>OR</b> <br><b>PEG-Filgrastim</b>6 mg SQ on day 2.<br>Repeat every 21 days', 'Moderate', 'Briasoulis E, Kalofonos H, Bafaloukos D, et al. Carboplatin plus paclitaxel in unknown primary Carcinoma: a Phase II Hellenic Cooperative Oncology Group Study. $J Clin Oncol. 2000; 18(17):3101-3107.$ ', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>paclitaxel:<br>For 3-hour infusion:                                                                                        If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)                                           If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup>                                                             If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup>                                                               If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use                                                                                 For 24-hour infusion:                                                                                      If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)                                                                        If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup>                                                              