INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (862, 168, 'DHAP', '<b>DHAP</b>', '<b>Dexamethasone</b> 40 mg/d PO or IV over 24 hours, days 1-4<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> CIVI, day 1<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 2 doses, day 2<br> Repeat every 21-28 days', 'High', 'Velasquez WS, Cabanillas F, Salvador P, et al. Effective salvage therapy for lymphoma with cisplatin in combination with high-dose Ara-C and dexamethasone (DHAP). $Blood. 1988;71(1):117-122.$', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li></ul>', 'CDDP<br>Ara-C<br>various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (863, 168, 'ESHAP', '<b>ESHAP</b>', '<b>Etoposide</b> 40 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2-3 hours,             day 5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>OR</b><br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-4<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2 hours, day 5, immediately following completion of cisplatin<br> Repeat every 21-28 days        ', 'High', 'Velasquez WS, McLaughlin P, Tucker S, et al. ESHAP- an effective chemotherapy regimen in refractory and relapsing lymphoma: a 4-year follow-up study. $J Clin Oncol. 1994; 12(6): 1169-1176.$<br> Rodrig', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'CDDP<br>Ara-C<br>VP-16<br>Depo-Medrol, Solu-Medrol, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (864, 168, 'Hyper-CVAD/MTX-Ara-C', '<b>Hyper-CVAD/MTX-Ara-C</b>', '<u>COURSE 1</u><br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> over 3 hours every 12 hours  for 6 doses , days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> over 48 hours after cyclophosphamide, days 4-5<br> <b>Vincristine</b> 2 mg administered 12 hours  after last cyclophosphamide dose, repeated on day 11<br> <b>Dexamethasone</b> 40 mg/d IV or PO,days 1-4,11-14<br> <b>Filgrastim</b> 5 mcg/kg IV or SQ beginning 24 hours after doxorubicin is complete.<br> <u>COURSE 2 <br>(BEGIN AFTER CLINICAL AND HEMATOLOGIC RECOVERY FROM COURSE 1)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus, day 1<br> <b>FOLLOWED BY</b><br> <b>Methotrexate</b> 800mg/m<sup>2</sup>over 24 hours day1 <b>Followed by</b><br><b>Cytarabine</b>3000 mg/m<sup>2</sup> IV every 12 hours for 4 doses, days 2-3 (dose reduced to 1,000 mg/m<sup>2</sup> in patients >60 years or with serum creatinine >1.5 mg/dL)<br> <b>Leucovorin calcium</b> 50 mg PO 24 hours after end of Methotrexate infusion<br> <b>FOLLOWED 6 HOURS LATER BY</b><br> <b>Leucovorin calcium</b> 15 mg PO every 6 hours for 8 doses (dose adjusted for Methotrexate concentration at 24 and 48 hours after completion of Methotrexate infusion)<br> <b>Filgrastim</b> 5mcg/kg IV or SQ begining 24 hours after cytarabine.<br> Repeat courses every 21 days.            ', 'High', 'Khouri IF, Romaguera J, Kantrajian H,et al. Hyper-CVAD and high -dose methotrexate/ cytarabine followed by stem-cell tans-plantation: an active regimen for aggressive mantle-cell lymphoma. $J Clin Onc', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Ara-C<br>various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (865, 168, 'R-EPOCH', '<b>R-EPOCH</b>', '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 65 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Vincristine</b> 0.5 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 5<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> Repeat every 21 days for 4 -6 cycles', 'Moderate', 'Jermann M, jost M, Taverna C, et al. Rituximab EPOCH, an effective salvage therapy for relapsed, refractory or transformed B-cell lymphomas: results of a phase II study. $Ann Oncol. 2004; 15(3):511-51', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Adriamycin, various<br>VP-16<br>various<br>Rituxan<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (866, 168, 'RICE', '<b>RICE</b>', '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 every 14 days<br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> with mesna 5,000 mg/m<sup>2</sup> CIVI over 24 hours, day 4<br> <b>Carboplatin</b> AUC of 5 (maximum 800 mg) IV, day 4<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 3-5<br> Repeat every 14 days', 'Moderate', 'Kewalramani T, Zelenetz AD, Nimer SD, Nimer SD, et al. Rituximab and ICE as second-line therapy before autologous stem cell transplantation for relapsed or primary refractory diffuse large B-cell lymp', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'Paraplatin<br>VP-16<br>Ifex<br>Mesnex, various<br>Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (867, 168, 'Bendamustine', '<b>Bendamustine</b>', '<b>Bendamustine</b> 120 mg/m<sup>2</sup> IV days 1 and 2<br> Repeat every 21 days', 'Moderate', 'Kahl BS, Bartlett NL, Leonard JP, et al. Bendamustine is effective therapy in patients with rituximab-refractory, indolent B-cell non-Hodgikin lymphoma: results from a multicenter study. $Cancer. 2010', '<b>Hepatic(Liver)</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use</li></ul>', 'Treanda');
