INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (880, 166, 'CDE', '<b>CDE</b>', '<b>Cyclophosphamide</b> 200mg/m<sup>2</sup>/d CIVI for 4 days<br><b>Doxorubicin</b> 12.5 mg/m<sup>2</sup>/d CIVI for 4 days<br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d CIVI for 4 days<br> Repeat every 28 days for a maximum of 8 cycles', 'Moderate', 'Sparano JA,Lee S, Chen MG, et al. Phase II trial of   infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Gr', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'Cytoxan<br>Adriamycin, various<br>VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (881, 166, 'CHOP', '<b>CHOP</b>', '<b>Cyclophosphamide</b> 750mg/m<sup>2</sup> IV, day 1<br> Doxorubicin 50 mg/m<sup>2</sup> IV, day 1<br><b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 100mg/m<sup>2</sup> PO, days 1-5<br> Repeat every 21 days.', 'Moderate', 'Weiss R, Mitrou P, Arasteh K, et.al. Acquired immunodeficiency syndrome-related lymphoma: Simultaneous treatment with combined cyclophosphamide, doxorubicin, Vincristine, and prednisone chemotherapy a', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Adriamycin, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (882, 166, 'CODOX-M/IVAC', '<b>CODOX-M/IVAC</b>', '<u>CYCLES 1 and 3 (CODOX-M)</u><br>  <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 200 mg/m<sup>2</sup>/d IV, days 2-5<br>  <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 8 for cycle 1,days 1, 8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 h, day 10, <b>THEN</b> 240 mg/m2/h CIVI for the next 23 hours<br> <b>Leucovorin calcium</b> 50 mg IV q6h, begining 36 hours from the start of Methotrexate until Methotrexate level <0.05 mcM<br> <b>Filgrastim</b> beginning 24 h from the start of leucovorin until ANC>1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1, 3<br> <b>Methotrexate<b> 12 mg IT, day 15<br> <u>CYCLES 2 AND 4 (IVAC)</u><br> <b>Ifosofamide</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Mesna</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Etoposide</b> 60 mg/m<sup>2</sup> IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV q 12h, days 1 and 2<br> <b>Filgrastim</b> beginning 24 h after completion of chemotherapy until ANC >1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 15<br> Additional IT Treatment for CNS disease- positive patients:<br> Cycle 1: cytarabine 70 mg IT, days 1,3,5, and Methotrexate 12 mg IT, days 15,17<br> Cycle 3: cytarabine 70 mg IT, days 1,3, and Methotrexate 12 mg IT, day 15                                                                                                                                                               ', 'Moderate', 'Wang ES, Strauss DJ, Teruya-Feldstein J, et al. Intensive chemotherapy with cyclophosphosphamide, doxorucibicin, high-dose methotrecate/ ifosfamide, etoposide, and high-dose cytarabine (CODOX-M/IVAC)f', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Ara-C<br>VP-16<br>Ifex<br>Mesnex, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (883, 166, 'EPOCH Dose-adjusted ', '<b>EPOCH Dose-adjusted </b>', '<b>Etoposide</b> 50 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Vincristine</b> 0.4 mg/m<sup>2</sup>/d /CIVI, days 1-4 (96 hours)<br> <b>Cyclophosphamide</b> <br>Cycle1: <br>375 mg/m<sup>2</sup> IV, day 5, if CD4+ cells >= 100/mm<sup>3</sup>; 187 mg/m<sup>2</sup> IV, day 5, if CD4+ cells<100/mm<sup>3</sup><br> After cycle 1: increase dose by 187 mg if nadir ANC > 500/mvL or platelets <25,000/mcL<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Filgrastim</b> 5 mcg/kg/d SQ, day 6, and until ANC >5,000/mcl<br> Repeat every 21 days', 'Moderate', 'Little RF, Pittaluga S, Grant N, et al. Highly effective treatment of acquired immunodeficiency syndrome-related lymphoma with dose-adjusted EPOCH: impact of antiretroviral therapy suspension and tumo', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>VP-16<br>various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (884, 169, 'CALGB 8811', '<b>CALGB 8811</b>', '<u>INDUCTION (COURSE 1:4 WEEKS)</u><br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day1 (800 mg/m<sup>2</sup> for patients >=60 years)<br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup> IV, days 1-3 (30 mg/m<sup>2</sup> for patients>=60 years)<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15, 22<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-21 (days 1-7 for patients >=60 years)<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 5, 8, 11, 15, 18, 22<br> <u>EARLY INTENSIFICATION (COURSE 2:4 WEEKS, REPEAT ONCE)</u><br> <b>Methotrexate</b> 15 mg IT, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 1-4, 8-11<br> <b>Vincristine</b> 2 mg IV, days 15, 22<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 15, 18, 22, 25<br> <u>CNS PROPHYLAXIS AND INTERIM MAINTENANCE (COURSE 3:12 WEEKS)</u><br> <b>Cranial RT</b> 2,400 cGy, days 1-12<br> <b>Methotrexate</b> 15 mg IT, days 1, 8, 15, 22, 29<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-70<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 36, 43, 50, 57, 64<br> <u>LATE INTENSIFICATION (COURSE 4:8 WEEKS)</u><br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, days 1, 8, 15<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15<br> <b>Dexamethasone</b> 10 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 29<br> <b>Thioguanine</b> 60 mg/m<sup>2</sup>/d PO, days 29-42<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 29-32, 36-39<br> <u>MAINTENANCE (COURSE 5:4 WEEKS, FOR 2 YEARS FROM DIAGNOSIS)</u><br> <b>Vincristine</b> 2 mg IV, day 1 of every 4 weeks<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5 of every 4 weeks<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 1, 8, 15, 22<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-28', 'Moderate', 'Larson RA, Dodge RK, Burns CP, et al. A five-drug remission induction regimen with intensive consolidation for adults with acute lymphoblastic leukemia: Cancer and Leukemia Group B study 8811. $Blood.', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50% <br>                                          If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Ara-C<br>various<br>Adriamycin, various<br>various<br>6-TG<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (885, 169, 'Hyper-CVAD', '<b>Hyper-CVAD</b>', '<u>ODD CYCLES (1, 3, 5, 7)</u><br>                            <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1-3<br> <b>Mesna</b> 600 mg/m<sup>2</sup>/d CIVI, days 1-3, ending 6 hours after cyclophosphamide completion<br> <b>Vincristine</b> 2 mg IV, days 4, 11<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 4<br> <b>Dexamethasone</b> 40 mg/d, days 1-4, 11-14<br> <b>Filgrastim</b> 5 mcg/kg bid SQ beginning 24 hours after doxorubicin completion <br> <b>OR</b><br><b>PEG-Filgrastim</b>6 mg SQ once 24 hours after doxorubicin completion<br> <u>EVEN CYCLES (2, 4, 6, 8)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV over 2 hours, day 1,<br> <b>FOLLOWED BY</b>  800 mg/m<sup>2</sup> IV over 24 hours<br> <b>Leucovorin calcium</b> 15 mg IV every 6 hours for 8 doses, beginning 24 hours after Methotrexate completion; adjust per Methotrexate level<br> <b>Cytarabine</b> 3 g/m<sup>2</sup> IV over 2 hours every 12 hours for 4 doses, days 2, 3<br> <b>Methylprednisolone</b> 50 mg IV bid, days 1-3<br> <b>Filgrastim</b> 6 mg/kg SQ beginning 24 hours after cytarabine completion<br> <b>OR</b><br><b>PEG-Filgrastim</b>6 mg SQ once 24 hours after doxorubicin completion<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 2 of each cycle<br> <b>Cytarabine</b> 100 mg IT, day 8 of each cycle<br>4-16 cycles based on risk profile', 'High', 'Kantarjian HM, O''Brien S, Smith TL, et al. Results of treatment with Hyper-CVAD, a dose-intensive regimen, in adult acute lymphocytic leukemia. $J Clin Oncol. 2000;18(3):547-561.$', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Ara-C<br>various<br>Adriamycin, various<br>Mesnex, various<br>Depo-Medrol, Solu-Medrol, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (886, 169, 'Nelarabine', '<b>Nelarabine</b>', '<b>Nelarabine</b> 1,500 mg/m<sup>2</sup>/d IV, days 1, 3 and 5', 'Low', 'Arranon [package insert]. $Research Triangle Park, NC: GlaxoSmithKline;2011.$', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (887, 170, 'ATRA/Arsenic', '<b>ATRA/Arsenic</b>', '<b>All-trans-retinoic acid (ATRA)</b> 45 mg/m<sup>2</sup> PO in 2 divided doses daily<br> <b>Arsenic trioxide</b> 0.15 mg/kg/d IV over 1 hour, daily beginning day 10 and continued until <5% blasts and absence of promyelocytes', 'Moderate', 'Estey E, Garcia-Manero G, Ferrajoli A, et al. Use of all-trans-retinoic acid plus arsenic trioxide as an alternative to chemotherapy in untreated acute promyelocytic leukemia. $Blood. 2006;107(9):3469', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (888, 170, 'ATRA/Idarubicin', '<b>ATRA/Idarubicin</b>', '<b>All-trans-retinoic acid</b> (ATRA) 45 mg/m<sup>2</sup> PO, daily<br> <b>Idarubicin</b> 12 mg/m<sup>2</sup> IV, on days 2, 4, 6, 8', 'Moderate', 'Sanz MA, Montesinos P, Vellenga E, et al. Risk-adapted treatment of acute promyelocytic leukemia with all-trans retinoic acid and anthracycline monochemotherapy: long-term outcomeon the LPA99 multicen', '<b>Hepatic(Liver)</b><ul><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25%</li></ul>', 'Idamycin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (889, 170, 'Idarubicin/Cytarabine/Etoposide', '<b>Idarubicin/Cytarabine/Etoposide</b>', '<b>Cytarabine</b> 25 mg/m<sup>2</sup> IV <b>FOLLOWED BY</b> 100 mg/m<sup>2</sup> CIVI, days 1-10<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-5<br> <b>Daunorubicin</b> 50 mg/m<sup>2</sup> IV, days 1, 3, 5<br> <b>OR</b><br> <b>Mitoxantrone</b> 12 mg/m<sup>2</sup> IV, days 1, 3, 5<br> <b>OR</b><br> <b>Idarubicin</b> 10 mg/m<sup>2</sup> IV, days 1, 3, 5', 'Moderate', 'Mandelli F, Vignetti M, Suciu S, et al. Daunorubicin versus mitoxantrone versus idarubicin as induction and consolidation chemotherapy for adults with acute myeloid leukemia: the EORTC and GIMEMA grou', '<b>Hepatic(Liver)</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50% <br>                                          If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use</li><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal(Kidney)</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25%</li><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Ara-C<br>VP-16<br>Idamycin<br>Novantrone');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (890, 170, '7 + 3', '<b>7 + 3</b>', '<b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7<br> <b>WITH</b><br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>OR</b><br> <b>Idarubicin</b> 13 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>OR</b><br> <b>Mitoxantrone</b> 12 mg/m<sup>2</sup>/d IV, days 1-3', 'Moderate', 'Presler H, Davis RB, Kirshener J, et al. Comparison of three remission induction regimens and two post-induction strategies for the treatment of acute nonlymphocytic leukemia: a Cancer and Leukemia Gr', '<b>Hepatic(Liver)</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25% <br>                                          If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50% <br>                                          If bilirubin >5 mg/dL, do not use </li><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use</li><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal(Kidney)</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25%</li><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Ara-C<br>Idamycin<br>Novantrone');
