INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (920, 174, 'Melphalan/Prednisone/Talidomide (MPT)', '<b>Melphalan/Prednisone/Talidomide (MPT)</b>', '<b>Melphalan</b> 0.25 mg/kg PO, daily for 5 days<br> <b>Prednisone</b> 1 mg/kg PO, daily for 5 days<br> Repeat every 28 days for 8 cycles<br> <b>Thalidomide 50 - 100 mg PO, daily ', 'Low', ' $Palumbo A, et al. Lancet 2006;28:367-835.$', '<b>Hepatic(Liver)</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Alkeran<br>various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (921, 174, 'RVD', '<b>RVD</b>', '<b>Lenalidomide</b> 25 mg/d PO, days 1-14<br> <b> Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Dexamethasone</b> 20 mg/d PO days 1, 2, 4, 5, 8, 9, 11, 12 (cycles 1-4)<br> Repeat every 21 days for 8 cycles', 'Low', 'Richardson PG, Weller E, Lonial S, et al. Lenalidomide, bortezomib, and dexamethasone combination therapy in patients with newly diagnosed multiple myeloma. $Blood. 2010;116(5):679-686.$', '<b>Hepatic(Liver)</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal(Kidney)</b><ul><li>Bortezomib:<br>No dose reduction</li><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency</li></ul>', 'Velcade<br>various<br>Revlimid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (922, 174, 'VAD', '<b>VAD</b>', '<b>Vincristine</b> 0.4 mg/d CIVI, days 1-4<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Dexamethasone</b> 40 mg/d PO, days 1-4, 9-12, 17-20<br> Repeat every 25 days', 'Moderate', 'Segeren CM, Sonneveld P, Holt V, et al. Vincristine, doxorubicin and dexamethasone (VAD) administered as rapid intravenous infusion for first-line treatment in untreated multiple myeloma. $Br J Haemat', '<b>Hepatic(Liver)</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Doxorubicin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction</li></ul>', 'various<br>Adriamycin, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (923, 174, 'VMP', '<b>VMP</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11, 22, 25, 29, 32 cycles 1-4<br> <b>FOLLOWED BY</b><br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 8, 22, 29 cycles 5-9<br> <b>Melphalan</b> 9 mg/m<sup>2</sup> PO, days 1-4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> PO, days 1-4<br> Repeat every 6 weeks for 9 cycles', 'Low', 'San Miguel JF, Schalg R, Khuageva NK, et al. MMY-3002 a phase 3 study comparing bortezomib-melphalan-prednisone (VMP) with melphalan-prednisone (MP) in newly diagnosed multiple myeloma. $Blood. 2007;1', '<b>Hepatic(Liver)</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Bortezomib:<br>No dose reduction</li><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Velcade<br>Alkeran<br>various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (924, 174, 'Bortezomib', '<b>Bortezomib</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1, 4, 8, 11<br> Repeat every 21 days ', 'Low', 'Richardson PG, Barlogie B, Berenson J, et al. A phase 2 study of bortezomib in relapsed, refractory myeloma. $N Engl J Med. 2003;348(26):2609-2617.$', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul><li>Bortezomib:<br>No dose reduction</li></ul>', 'Velcade');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (925, 174, 'Dexamethasone', '<b>Dexamethasone</b>', '<b>Dexamethasone</b> 20 mg/m<sup>2</sup>/d PO, days 1-4, 9-12, 17-20<br> Repeat after 14-day rest period', 'N/A', 'Alexanian R, Dimopoulos MA, Delasalle K, Barlogie B. Primary dexamethasone treatment of multiple myeloma. $Blood. 1992;80(4):887-890.$', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul></ul>', 'various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (926, 174, 'Thalidomide', '<b>Thalidomide</b>', '<b>Thalidomide</b> 200 mg/d PO at bedtime (increased dose every 2 weeks for 6 weeks up to a dose of 800 mg/d)', 'Low', 'Singhal S, Mehta J, Desikan R, et al. Antitumor activity of thalidomide in refractory multiple myeloma. $N Engl J Med. 1999;341(21):1565-1571.$ $ Erratum in N Engl J Med. 2000;342(5):364.$', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul></ul>', 'Thalomid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (927, 174, 'Carfilzomib', '<b>Carfilzomib</b>', '<b>Carfilzomib</b> 20 mg/m<sub>2</sub>, Days 1,2,8,9,15, and 16, followed by a 12 day rest period<br> Repeat cycle every 28 days', 'Low', 'Siegel DS, et al. A phase 2 study of single-agent carfilzomib (PX-171-003-A1) in patients with relapsed and refractory multiple myeloma.$Blood. 2012 Oct 4;120(14):2817-25. doi: 10.1182/blood-2012-05-4', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (928, 174, 'Pomalidamide', '<b>Pomalidamide</b>', '<b>Pomalidamide</b>4 mg PO daily, from Day 1-21 <br>Repeat cycle every 28 days', 'Low', 'Leleu X, et al. Pomalidomide plus low-dose dexamethasone is active and well tolerated in bortezomib and lenalidomide-refractory multiple myeloma: Intergroupe Francophone du Myélome 2009-02.$Blood. 201', '<b>Hepatic(Liver)</b><ul></ul><b>Renal(Kidney)</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (929, 175, 'CDDP/VP-16', '<b>CDDP/VP-16</b>', '<b>Cisplatin</b> 90 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 150 mg/m<sup>2</sup>/d IV, days 3, 4<br> Repeat every 21 days for 4 courses', 'High', 'Kovnar EH, Kellie SJ, Horowitz ME, et al. Preirradiation cisplatin and etoposide in the treatment of high-risk medulloblastoma and other malignant embryonal tumors of the central nervous system: a pha', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li></ul>', 'CDDP<br>VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (930, 175, 'COPE or "Baby Brain I"', '<b>COPE or "Baby Brain I"</b>', '<u>CYCLE A</u><br> <b>Vincristine</b> 0.065 mg/kg/d (maximum, 1.5 mg) IV, days 1, 8<br> <b>Cyclophosphamide</b> 65 mg/kg/d IV over 30 minutes, day 1<br> <u>CYCLE B</u><br> <b>Cisplatin</b> 4 mg/kg/d IV over 6 hours, day 1<br> <b>Etoposide</b> 6.5 mg/kg/d IV over 1 hour, days 3, 4<br> Regimens given in alternating 28-day cycles in the sequence AABAAB', 'High', 'Duffner PK, Horowitz ME, Krischer JP, et al. Postoperative chemotherapy and delayed radiation in children less than 3 years of age with malignant brain tumors. $N Engl J Med. 1993;328(24):1725-1731.', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vincristine:<br>No dose reduction</li></ul>', 'CDDP<br>Cytoxan<br>VP-16<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (931, 175, 'Vincristine/Cyclophosphamide/Cisplatin', 'Vincristine/Cyclophosphamide/Cisplatin', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> Repeat every 6 weeks for 8 cycles', 'High', 'Douglas JG, Barker JL, Ellenbogen RG, et al. Concurrent chemotherapy and reduced-dose cranial spinal irradiation followed by conformal posterior fossa tumor bed boost for average-risk medulloblastoma:', '<b>Hepatic(Liver)</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%   <br>                                                                   If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vincristine:<br>No dose reduction</li></ul>', 'CDDP<br>Cytoxan<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (932, 175, 'Carbo/VP-16/Vincristine', '<b>Carbo/VP-16/Vincristine</b>', '<u>CYCLES 1 AND 3 (3-WEEK CYCLES)</u><br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 7, 14<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-3<br> <b>Carboplatin</b> 500 mg/m<sup>2</sup> IV, days 1, 2<br><br> <u>CYCLES 2 AND 4 (3-WEEK CYCLES)</u><br> <b>Vincristine<sup>d</sup></b> 1.5 mg/m<sup>2</sup> IV, days 1, 7, 14<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-3<br> <b>Cyclophosphamide</b> 1,500 mg/m<sup>2</sup> IV, day 1<br> <b>Mesna</b> 750 mg/m<sup>2</sup> IV, 15 minutes before and 4 and 8 hours after cyclophosphamide', 'High', 'Taylor RE, Bailey CC, Robinson K, et al. Results of a randomized study of preradiation chemotherapy versus radiotherapy alone for nonmetastatic medulloblastoma: the International Society of Paediatric', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Paraplatin<br>Cytoxan<br>VP-16<br>Mesnex, various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (933, 175, 'POC', '<b>POC</b>', '<b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Lomustine</b> 100 mg/m<sup>2</sup> PO, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup>/d IV, days 1, 8, 15', 'Moderate', 'Finlay JL, Bayett JM, Yates AJ, et al. Randomized phase III trial in childhood high-grade astrocytoma comparing vincristine, lomustine, and prednisone with the eight-drugs-in-1-day regimen. $J Clin On', '<b>Hepatic(Liver)</b><ul><li>Lomustine:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Lomustine:<br>If CrCl < 60 mL/min, do not use</li><li>Vincristine:<br>No dose reduction</li></ul>', 'CCNU<br>various<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (934, 175, 'Carboplatin', '<b>Carboplatin</b>', '<b>Carboplatin</b> 560 mg/m<sup>2</sup> IV over 1 hour<br> Repeat every 28 days', 'High', 'Friedman HS, Krischer JP, Burger P, et al. Treatment of children with progressive or recurrent brain tumors with carboplatin or iproplatin: a Pediatric Oncology Group randomized phase II study. $J Cli', '<b>Hepatic(Liver)</b><ul><li>Carboplatin:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Carboplatin:<br>Dosing based on AUC </li></ul>', 'Paraplatin');
