INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (948, 181, 'Topo/CTX', '<b>Topo/CTX</b>', '<b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>FOLLOWED BY</b><br> <b>Topotecan</b> 0.6-0.75 mg/m<sup>2</sup>/d IV as an escalating dose, days 1-5', 'High', 'Saylors RL, Stewart CF, Zamboni WC, et al. Phase I study of topotecan in combination with cyclophosphamide in pediatric patients with malignant solid tumors: a Pediatric Oncolgy Group study. $J Clin O', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Topotecan:<br>No dose reduction</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                         If CrCl 20-39 mL/min, reduce to 0.75 mg/m<sup>2</sup> </li></ul>', 'Cytoxan<br>Hycamtin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (949, 181, 'VAC', '<b>VAC</b>', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV weekly for 3 weeks<br> <b>Dactinomycin</b> 0.015 mg/kg/d (maximum, 0.5 mg/kg/d) IV for 5 days<br> <b>Cyclophosphamide</b> 2,200 mg/m<sup>2</sup> IV, day 1', 'High', 'Crist WM, Anderson JR, Meza JL, et al. Intergroup rhabdomyosarcoma study-IV: results for patients with nonmetastatic disease. $J Clin Oncol. 2001;19(12):3091-3102.$', '<b>Hepatic(Liver)</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use</li><li>Dactinomycin:<br>If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50%</li><li>Vincristine:<br>No dose reduction</li></ul>', 'Cytoxan<br>Actinomycin<br>Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (950, 182, 'ABVD', '<b>ABVD</b>', '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 14 days for 12 courses', 'High', 'Fryer CJ, Hutchinson RJ, Krailo M, et al. Efficacy and toxicity of 12 courses of ABVD therapy followed by low-dose regional radiation in advanced Hodgkin''s disease in children: a report from the Child', '<b>Hepatic(Liver)</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use</li></ul><b>Renal(Kidney)</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50%</li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%  <br>                                                                    If CrCl < 10 mL/min, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction</li><li>Vinblastine:<br>No dose reduction</li></ul>', 'Blenoxane<br>DTIC<br>Adriamycin, various<br>Velban');
