INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Carbo-Tax</b>','<b>Carboplatin</b><br/> dose targeted by calvert equation to AUC of <p style="font-weight:bold;">OR</p> 6 mg/ml/min IV over 30 minutes.<br><b>FOLLOWED  BY</b><br><b>paclitaxel<sup>a</sup></b> 200mg/m<sup>2</sup>  IV over 3 hours, day 1<br><b> Filgrastim</b> 300 mcg/d SQ, days 5-12<br>Repeat every 21 days','Moderate','Briasoulis E, Kalofonos H, Bafaloukos D, et al. Carboplatin plus paclitaxel in unknown primary Carcinoma: a Phase II Hellenic Cooperative Oncology Group Study. $J Clin Oncol. 2000; 18(17):3101-3107.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Docetaxel/Carboplatin</b>','<b>Docetaxel<sup>b</sup></b> 75 mg/m<sup>2</sup> IV over 30 minutes, day1<br><b>Carboplatin</b> AUC of 5 mg/mL/min IV over 30 minutes, day 1<br>Repeat every 21 days for a maximum of 8 countries','Moderate','Pentheroudakis G, Briasoulis E, Kalofonos HP, et al. Docetaxel and carboplatin combination chemotherapy as outpatient palliative therapy in carcinoma of unknown primary: a multicentre Hellenic Cooperative Oncology Group Phase II study. $Acta Oncol. 2008;47(6): 1148-1155.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Gemcitabine/Carboplatin/paclitaxel</b>','<b>Gemcitabine</b> 1,000mg/m<sup>2</sup>  1V, days 1,8<br><b>Carboplatin</b> dose targeted by Calvert equation to AUC of 5 IV, day 1<br><b>Paclitaxel<sup>a</sup></b> 200 mg/m2 IV over 1 hour, day 1<br>Repeat cycle every 21 days','Moderate','Greco FA, Burris HA III, Litchy S, et al. Gemcitabine, carboplatin, and paclitaxel for patients with carcinoma of unknown primary site: a Minnie pearl cancer center research Network study. J Clin Oncol.2002;20(6):1651-1656.');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Gemcitabine/Cisplatin</b>','<b>Gemcitabine</b> 1,250 mg/m<sup>2</sup> IV, days 1 and 8<br><b>Cisplatin</b> 100mg/m<sup>2</sup> IV, day 1<br>Repeat every 21 days','High','Culine S, Lortholary A, Voigt JJ, et al. Gemcitabine, carboplatin combintion with either gemcitabine or irinotecan in carcinomas of unknown primary site: results of a randomized phase II study- trial for the French study Group on Carcinomas of Unknown Primary (GEFCAPI 01). $JClin Oncol. 2003;21(18):3479-3482.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Gemcitabine/Docetaxel</b>','<b>Gemcitabine</b> 1,000mg/m<sup>2</sup> IV over 30 minutes, days 1 and 8<br><b>Docetaxel<sup>b</sup></b> 75 mg/m<sup>2</sup>  IV over 1 hour, day 8<br>Repeat every 21 days for a maximum of 6 cycles','Low','Pouessel D, Culine S, Becht C, et al. Gemcitabine and docetaxel as front-line chemotherapy in patients with carcinoma of an unknown primary site. $Cancer. 2004; 100(6): 1257-1261.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Oxaliplatin Capecitabine</b>','<b>Oxaliplatin</b> 130 mg/m<sup>2</sup>  IV, day 1<br><b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br>Repeat cycle every 21 days for 6 cycles                                                 ','Moderate','Hainsworth JD, Spigel DR, Burris HA, et al. Oxaliplatin and capecitabine inn the treatment of patients with recurrent or refractory carcinoma of unknown primary site: A phase 2 trial of the Sarah Cannon Oncology Research Consortium. $Cancer. 2010; 116(10):2448-2454.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(10,'<b>Paclitaxel/Carboplatin/Etoposide</b>','<b>Paclitaxel<sup>a</sup></b>  200mg/m<sup>2</sup> IV over 1 hour, day 1,<br><b>FOLLOWED BY</b><br> <b>Carboplatin</b> dose targeted by Calvert equation to AUC of 6 mg/mL/min IV, day 1<br><b>Etoposide</b> 50 mg/d PO alternated with 100mg/d PO, days 1-10<br> Repeat every 21 days','Moderate','Hainsworth JD, Spigel DR, Litchy S, Greco FA. Phase II trial of paclitaxel, carboplatin, and etoposide in advanced poorly differentiated neutroendocrine carcinoma: a Minnie Pearl Cancer Network Study. $J Clin Oncol. 2006;24(22):3548-3554.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(20,'<b>Interferon alfa-2b</b>','<b>Interferon alfa-2b<sup>c</sup></b>  30 million IU/m<sup>2</sup> IM <b>OR</b> SQ 3 times per week<br>Repeat until disease progression or maximal response has been achieved after 16 weeks','Moderate','Intron A [Package insert]. $Kenilworth, NJ: Schering-Plough; 2011.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(20,'<b>Liposomal daunorubicin</b>','<b>Liposomal daunorubicin</b> 40 mg/m<sup>2</sup> IV over 60 minutes<br>  Repeat every 14 days','Moderate','Rosenthal E, Poizot-Martin I, Saint Marc T, et al. Phase Ivstudy of liposomal daunorubicin (DaunoXome) in AIDS-related Kaposi sarcoma. $AM J Clin Oncol.2002;25(1): 57-59$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(20,'<b>Paclitaxel</b>','<b>Paclitaxel<sup>a</sup></b>  135 mg/m<sup>2</sup> IV over 3 hours<br> Repeat every 21 days<br><b>OR</b><br> <b>Paclitaxel<sup>a</sup></b> 100 mg/m<sup>2</sup> IV over 3 hours<br>Repeat every 14 days   ','Low','Taxol [Package insert]. $Princeton, NJ: Bristol- Myers Squibb Oncology; 2011.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(20,'<b>Pegylated liposomal doxorubicin</b>','<b>Pegylated liposomal doxorubicin</b> 20 mg/m<sup>2</sup> IV every 3 weeks as tolerated at an initial rate of 1 mg/min; if no infusion- related adverse events are observed, the infusiion rate can be increased to complete administration over 1 hour.','Low','Daxil [Package insert]. $Raritan, NJ: Centocor Ortho Biotech Products, LP; 2010.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(30,'<b>CDE</b>','<b>Cyclophosphamide</b> 200mg/m<sup>2</sup>/d CIVI for 4 days<br><b>Doxorubicin</b> 12.5 mg/m<sup>2</sup>/d CIVI for 4 days<br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d CIVI for 4 days<br> Repeat every 28 days for a maximum of 8 cycles','Moderate','Sparano JA,Lee S, Chen MG, et al. Phase II trial of infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Group Trial (E1491). $J Clin Oncol. 2004; 22(8): 1491-1500.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(30,'<b>CHOP</b>','<b>Cyclophosphamide</b> 750mg/m<sup>2</sup> IV, day 1<br> Doxorubicin 50 mg/m<sup>2</sup> IV, day 1<br><b>Vincristine<sup>d</sup></b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 100mg/m<sup>2</sup> PO, days 1-5<br> Repeat every 21 days; doses of cyclophosamide and doxorubicin may be reduced up to 25% in high-risk patients','Moderate','Weiss R, Mitrou P, Arasteh K, et.al. Acquired immunodeficiency syndrome-related lymphoma: Simultaneous treatment with combined cyclophosphamide, doxorubicin, Vincristine, and prednisone chemotherapy and highly active antiretroviral therapy is safe and improves survival--results of the German Multicenter Trial. $Cancer. 2006; 106(7): 1560-1568.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(30,'<b>CODOX-M/IVAC</b>','<u>CYCLES 1 and 3 (CODOX-M)</u><br>  <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide<b> 200 mg/m<sup>2</sup>/d IV, days 2-5<br>  <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine<sup>b</sup></b> 1.5 mg/m<sup>2</sup> IV, days 1, 8 for cycle 1,days 1, 8, 15 for cycle 2<br> <b>MTX</b> 1,200 mg/m<sup>2</sup> IV over 1 h, day 10, <b>THEN</b> 240 mg/m2/h CIVI for the next 23 hours<br> <b>Leucovorin calicium</b> 50 mg IV q6h, begining 36 hours from the start of MTX until MTX level <0.05 mcM<br> <b>Filgrastim</b> beginning 24 h from the start of leucovorin until ANC>1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1, 3<br> <b>MTX<b> 12 mg IT, day 15<br> <u>CYCLES 2 AND 4 (IVAC)</u><br> <b>Ifosofamide</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Mesna</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Etoposide</b> 60 mg/m<sup>2</sup> IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV q 12h, days 1 and 2<br> <b>Filgrastim</b> beginning 24 h after completion of chemotherapy until ANC >1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>MTX</b> 12 mg IT, day 15<br> Additional IT Treatment for CNS disease- positive patients:<br> Cycle 1: cytarabine 70 mg IT, days 1,3,5, and MTX 12 mg IT, days 15,17<br> Cycle 3: cytarabine 70 mg IT, days 1,3, and MTX 12 mg IT, day 15                                                                                                                                                               ','Moderate','Wang ES, Strauss DJ, Teruya-Feldstein J, et al. Intensive chemotherapy with cyclophosphosphamide, doxorucibicin, high-dose methotrecate/ ifosfamide, etoposide, and high-dose cytarabine (CODOX-M/IVAC)for human immunodeficiency virus-associated Burkitt lymphoma. $Cancer. 2003;98(6): 1196-1205.$');
INSERT INTO RegimenDetail(parentPK, name, schedule, emetogenic_potential, reference) VALUES(30,'<b>Dose-adjusted EPOCH</b>','<b>Etoposide</b> 50 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Doxrubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Vincristine<sup>d</sup></b> 0.4 mg/m<sup>2</sup>/d /CIVI, days 1-4 (96 hours)<br> <b>Cyclophosphamide</b> Cycle1: 375 mg/m<sup>2</sup> IV, day 5, if CD4+ cells >= 100/mm<sup>3</sup>; 187 mg/m<sup>2</sup> IV, day 5, if CD4+ cells<100/mm<sup>3</sup><br> After cycle 1: increase dose by 187 mg if nadir ANC > 500/mvL or platelets <25,000/mcL<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Filgrastim</b> 5 mcg/kg/d SQ, day 6, and until ANC >5,000/mcl<br> Repeat every 21 days','Moderate','Little RF, Pittaluga S, Grant N, et al. Highly effective treatment of acquired immunodeficiency syndrome-related lymphoma with dose-adjusted EPOCH: impact of antiretroviral therapy suspension and tumor biology. $BLOOD.2003;101(12):4653-4659.$');
