INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (66, 4, 'XELIRI', '<b>XELIRI</b>', '<b>Irinotecan</b> 250 mg/m<sup>2</sup> IV over 30-90 minutes, day 1<br> <b>Cappecitabine 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat cycle every 21 days', 'Moderate', 'Patt YZ, Lee F, Liebman JE, et al. Capecitabine plus 3-weekly irinotecan (XELIRI regimen) as first-line chemotherapy for metastatic colorectal cancer. $Am J Clin Oncol. 2007;30(4):350-357$', '<b>Hepatic Impairment</b><ul><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Irinotecan:<br>No dose reduction </li></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (67, 4, 'XELOX', '<b>XELOX</b>', '<b>Oxaliplatin</b> 130 mg/m<sup>2</sup> IV, day 1<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid from evening day 1 to 14<br> Repeat every 21 days', 'Moderate', 'Cassidy J, Tabernero J, Twelves C, et al. XELOX (capecitabine plus oxaliplatin); active first-line therapy for patients with metastatic colorectal cancer. $J Clin Oncol. 2004;22(11):2084-2091$', '<b>Hepatic Impairment</b><ul><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (68, 4, 'XELOX + Bevacizumab', '<b>XELOX + Bevacizumab</b>', ' <b>Oxaliplatin</b> 130 mg/m<sup>2</sup> IV, day 1<br><b>Bevacizumab</b> 7.5 mg/kg IV, day 1<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid from evening day 1 to 14<br> Repeat every 21 days<br> Repeat every 21 days', 'Moderate', 'Saltz LB, Clarke S, Diaz-Rubio E, et al. Bevacizumab in combination with oxaliplatin-based chemotherapy as first-line therapy in metastatic colorectal cancer: a randomized phase III study. $J Clin Oncol. 2008;26(18)2013-2019.$', '<b>Hepatic Impairment</b><ul><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Bevacizumab : Avastin<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (69, 4, 'Capecitabine', '<b>Capecitabine</b>', '<b>Capecitabine</b> 1,000- 1,250 mg/m<sup>2</sup>/d PO bid, days 1-14, <b>FOLLOWED BY</b> 7 days rest<br> Repeat every 21 days', 'Low', 'Van Cutsem E, Twelves C, Cassidy J, et al. Oral capecitabine compared with intravenous fluorouracil plus leucovorin in patients with metastatic colorectal cancer: results of a large Phase III Study. $J Clin Oncol. 2001;19(21):4097-4106$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li></ul>', 'Capecitabine : Xeloda');
