INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (77, 8, 'Imatinib', '<b>Imatinib</b>', '<b>Imatinib mesylate</b> 400-800 mg PO, daily', 'Low', 'Blanke CD, Rankin C, Demetri GD, et al. Phase III randomized, intergroup trial assessing imatinib mesylate at two dose levels in patients with unresectable or metastatic gastrointestinal stromal tumors expressing the Kit receptor tyrosine kinase: S0033. $J Clin Oncol. 2008;26(4):626-632$', '<b>Hepatic Impairment</b><ul><li>Imatinib:<br>If bilirubin < 3 upper normal limit, no dose reduction <br>                            If bilirubin > 3 upper normal limit, use with caution </li></ul><b>Renal Impairment</b><ul><li>Imatinib:<br>No dose reduction </li></ul>', 'Imatinib : Gleevec');
