INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (79, 8, 'Sunitinib', '<b>Sunitinib</b>', '<b>Sunitinib malate</b> 50 mg/d for 28 days, 14 days rest then repeat cycle', 'Low', 'Demetri GD, van Oossterom AT, Garrett CR, et al. Efficacy and safety of sunitinib in patients with advanced gastrointestinal stromal tumour afer failure of imatinib: a randomised controlled trial. $Lancet. 2006;368(9544):1329-1338.$', '<b>Hepatic Impairment</b><ul><li>Sunitinib:<br>If Child-Pugh A or B, no dose reduction  <br>                                                    If Child-Pugh C, reduce dose by 50% </li></ul><b>Renal Impairment</b><ul><li>Sunitinib:<br>If CrCl > 42 mL/min, no dose reduction </li></ul>', 'Sunitinib : Sutent');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (80, 12, 'Carbo-Tax', '<b>Carbo-Tax</b>', '<b>Paclitaxel</b> 225mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Carboplatin</b> dose targeted by AUC of 6 mg/mL/min, day 1<br> Repeat every 21 days', 'Moderate', 'Schiller JH, Harrington D, Belani CP, et al. Comparison of four chemotherapy regimens for advanced non-small-cell lung cancer. $N Engl J Med. 2002;346(2):92-98.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                               