INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (81, 12, 'Cisplatin/Pemetrexed', '<b>Cisplatin/Pemetrexed</b>', '<b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Pemetrexed</b> 500 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 6 cycles', 'High', 'Scagliotti GV, Parikh P, von Pawel J, et al. Phase III study comparing cisplatin plus gemcitabine with cisplatin plus pemetrexed in chemotherapy-naïve patients with advanced-stage non-small-cell lung cancer. $J Clin Oncol. 2008;26(21):3543-3551.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Pemetrexed:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Pemetrexed:<br>If CrCl > 45 mL/min, no dose reduction    <br>                                                                        If CrCl < 45 mL/min, do not use </li></ul>', 'Cisplatin : CDDP<br>Pemetrexed : Alimta');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (82, 12, 'Cisplatin/Vinorelbine/Cetuximab', '<b>Cisplatin/Vinorelbine/Cetuximab</b>', '<b>Cetuximab</b> 400 mg/m<sup>2</sup> IV initial dose, <b>THEN</b><br> 250 mg/m<sup>2</sup>/wk<br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1<br> <b>Vinorelbine</b> 25 mg/m<sup>2</sup> IV, days 1, 8<br> Repeat every 21 days', 'High', 'Pirker R, Pereira JR, Szczesna A, et al. Cetuximab plus chemotherapy in patients with advanced non-small-cell lung cancer (FLEX): an open-label randomised phase III trial. $Lancet. 2009;373(9674):1525-1531.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Vinorelbine:<br>If bilirubin 2-3 mg/dL, reduce dose by 50%    <br>                                           If bilirubin 3.1-5 mg/dL, reduce dose by 75%      <br>                                      If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Vinorelbine:<br>No dose reduction</li></ul>', 'Cetuximab : Erbitux<br>Cisplatin : CDDP<br>Vinorelbine : Navelbine');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (83, 12, 'Docetaxel/Cisplatin', '<b>Docetaxel/Cisplatin</b>', '<b>Docetaxel</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days', 'High', 'Schiller JH, Harrington D, Belani CP, et al. Comparison of four chemotherapy regimens for advanced non-small-cell lung cancer. $N Engl J Med. 2002;346(2):92-98.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Docetaxel : Taxotere');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (84, 12, 'Gemcitabine/Cisplatin', '<b>Gemcitabine/Cisplatin</b>', '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup> /d IV,days 1,8,15<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> Repeat every 28 days<br> <b>OR</b><br> <b>Gemcitabine</b> 1,250 mg/m<sup>2</sup>/d IV over 30 minutes, days 1, 8<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> IV over 60 minutes, day 1<br> Repeat every 21 days                                         ', 'High', 'Schiller JH, Harrington D, Belani CP, et al. Comparison of four chemotherapy regimens for advanced non-small-cell lung cancer. $N Engl J Med. 2002;346(2):92-98.$<b> Cardenal F, Lopez-Cabrerizo MP, Anton A, et al. Randomized phase III study of gemcitabine-cisplatin versus etoposide-cisplatin in the treatment of locally advanced or metastatic non-small-cell lung cancer. $J Clin Oncol. 1999;17(1):12-18.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (85, 12, 'Gemcitabine/Docetaxel', '<b>Gemcitabine/Docetaxel</b>', '<b>Gemcitabine</b> 1,100 mg/m<sup>2</sup>/d IV over 30 minutes, days 1, 8<br> <b>Docetaxel</b> 100 mg/m<sup>2</sup> IV over 1 hour, day 8<br>  Repeat every 21 days.', 'Low', 'Georgoulias V, Papadakis E, Alexopoulos A, et al. Platinum-based and non-platinum-based chemotherapy in advanced non-small-cell lung cancer: a randomized multicentre trial. $Lancet. 2001;357(9267):1478-1484.$', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Docetaxel : Taxotere<br>Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (86, 12, 'Gemcitabine/Paclitaxel', '<b>Gemcitabine/Paclitaxel</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup>/d IV over 30 minutes, days 1, 8<br> <b>Paclitaxel</b> 200 mg/m<sup>2</sup> IV over 3 hours, day 1<br> Repeat every 21 days', 'Low', 'Kosmidis P, Mylonakis N, Nicolaides C, et al. Paclitaxel plus carboplatin versus gemcitabine plus paclitaxel in advanced non-small-cell lung cancer: a phase III randomized trial. $J Clin Oncol. 2002;20(17):3578-3585.$', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Gemcitabine : Gemzar, various<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (87, 12, 'Gemcitabine/Vinorelbine', '<b>Gemcitabine/Vinorelbine</b>', '<b>Gemcitabine</b> 1,200 mg/m<sup>2</sup>/d IV, days 1, 8<br> <b>Vinorelbine</b> 30 mg/m<sup>2</sup>/d IV, days 1, 8<br> Repeat every 21 days<br> <b>OR</b><br> <b>Gemcitabine</b> 800-1,000 mg/m<sup>2</sup>/d IV over 30 minutes, days 1, 8, 15<br> <b>Vinorelbine</b> 20 mg/m<sup>2</sup>/d IV over 10-30 minutes, days 1, 8, 15<br> Repeat every 28 days', 'Low', 'Frasci G, Lorusso V, Panza N, et al. Gemcitabine plus vinorelbine versus vinorelbine alone in elderly patients with advanced non-small-cell lung cancer. $J Clin Oncol. 2000;18(13):2529-2536.$<br> Chen YM, Perng, RP, Yang KY, et al. A multi-gemcitabine in previously untreated inoperable (stageIIIB/IV) non-small-cell lung cancer. $Chest. 2000;117(6):1583-1589.$<br> Hainsworth JD, Burris HA 3rd, Litchy S, et al. Gemcitabine and vinorelbine in the second-line treatment of non-small-celll lung carcinoma patients: a Minnie Pearl Cancer Research Network phase II trial. $Cancer. 2000;88(6):1353-1358.$', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Vinorelbine:<br>If bilirubin 2-3 mg/dL, reduce dose by 50%    <br>                                           If bilirubin 3.1-5 mg/dL, reduce dose by 75%      <br>                                      If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Vinorelbine:<br>No dose reduction</li></ul>', 'Gemcitabine : Gemzar, various<br>Vinorelbine : Navelbine');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (88, 12, 'PC', '<b>PC</b>', '<b>Paclitaxel</b> 135mg/m<sup>2</sup> over 24hours, day 1<br> <b>Cisplatin</b> 75mg/m<sup>2</sup> IV, day 2<br> Repeat every 21 days<br> <b>OR</b><br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> over 3 hours,day 1<br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days   ', 'High', 'Schiller JH, Hamington D, Belani CP, et al. Comparison of four chemotherapy regiments for advanced non-small-cell lung cancer. $N Eng/J Med.2002;346(2);92-98.$<br> Smit EF., van Meerbeeck JP,lianes P,et al.Three-arm randomaized study of two cisplatin-based regimens and paclitaxel plus gemcitabine in advance m=non-small-cell lung cancer: a phase III trial of the European Organization for Research and Treatment of cancer Lung Group-EORTC 08975. $J Clin Oncol.2003;21(21):3909-3917.$                                                                                                                                                ', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Cisplatin : CDDP<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (89, 12, 'PCB', '<b>PCB</b>', '<b>Paclitaxel</b> 200 mg/m<sup>2</sup> IV, day 1<br> <b>Carboplatin</b> AUC of IV ,day 1<br> <b>Bevacizumab</b> 15 mg/kg IV, day 1<br> Repeat every 21 days', 'Moderate', 'Sandler A, Gray R, Perry MC,et al. Paclitaxel-carboplatin alone or with bevacizumab for non small-cell lung cancer. $N Engl J Med. 2006;355(24):2542-2550.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                             