INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (90, 12, 'Vinorelbine/Cisplatin', '<b>Vinorelbine/Cisplatin</b>', '<b>Vinorelbine</b> 30 mg/m<sup>2</sup> IV over 20 minutes, day 1,8,15,22.<br> <b>Cisplatin</b> 100mg/m<sup>2</sup> IV over 1 hour day 1.<br>Repeat cycle every 28 days.', 'High', 'Douillard JY, Rosell R, De Lena M,et al. Adjuvant vinorelbine plus cisplatin versus observation in patients with completely resected stage IB-III A non small-cell lung cancer  (Adjuvant Naverlbine International Trialist Association [ANITA): a randomised controlled trial. $Lancet Oncol.2006:7(9):719-727.$ ', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Vinorelbine:<br>If bilirubin 2-3 mg/dL, reduce dose by 50%    <br>                                           If bilirubin 3.1-5 mg/dL, reduce dose by 75%      <br>                                      If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Vinorelbine:<br>No dose reduction</li></ul>', 'Cisplatin : CDDP<br>Vinorelbine : Navelbine');
