INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (97, 13, 'Carboplatin/Irinotecan', '<b>Carboplatin/Irinotecan</b>', '<b>Carboplatin</b> AUC of 5 IV, day 1<br> <b>Irinotecan</b> 175mg/m<sup>2</sup> IV, day 1<br> Repeat every 21days', 'Moderate', 'Hermes A, Bergman B, Bremnes R, et al           Irinotecan plus carboplatin in extensive versus oral etoposide plus carboplatin in extensive small-cell lung cancer: a randomized phase III trial. $J Clin Oncol. 2008:26(26):4261-4267.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Irinotecan:<br>No dose reduction </li></ul>', 'Carboplatin : Paraplatin');
