INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (99, 13, 'CAV', '<b>CAV</b>', '<b>Cyclophosphamide</b>  1,000 mg/m<sup>2</sup>IV on day 1.  <br>                                              <b>Doxorubicin</b> 40 mg/m<sup>2</sup>IV on day 1.<br>                                                                <b>Vincristine</b> 1 mg/m<sup>2</sup>IV on day 1. (maximum, 2 mg) <br>Repeat cycle every 21 days.', 'High', '$Roth  BJ, et al. J Clin Oncol 1992;10:282-291.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (100, 13, 'Carboplatin + Paclitaxel', '<b>Carboplatin + Paclitaxel</b>', '<b>Carboplatin</b>AUC of 2, IV on days1,8, and 15.<br><b>Paclitaxel</b> 80 mg/m<sup>2</sup> IV on days 1,8, and 15. Repeat cycle every 28 days.', 'Moderate', '$Neubauer M, et al. J Clin Oncol2004;22:1872-1877$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (101, 13, 'EC', '<b>EC</b>', '<b>Etoposide</b> 100mg/m<sup>2</sup>/d IV, days 1-3<br><b>Carboplatin</b> 450mg/m<sup>2</sup> IV, day 1<br> Repeat every 28 days for 6 cycles', 'Moderate', 'Viren M, Liippo K, Ojala a, et al. Carboplatin and etoposide in extensive small-cell lung cancer. $Acta Oncal. 1994;33(8):921-924.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Carboplatin : Paraplatin<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (102, 13, 'EP', '<b>EP</b>', '<b>Etoposide</b> 80mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cisplatin</b> 80mg/m<sup>2</sup> IV, day 1 (Standard dose)<br> Repeat every 21 days <b>OR</b><br> <b>Etoposide</b> 80mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 27mg/m<sup>2</sup> IV, days 1-5(high dose)<br> Repeat every 21 days', 'High', 'Ihde DC, Mulshine JL, Kramer BS, et al.  Prospective randomized comparision of high-dose and standard-dose etoposide and cisplatin chemotherapy in patients with extensive-stage small-cell lung cancer. $J Clin Oncol.1994; 12(10):2022-2034.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (103, 13, 'Etoposide', '<b>Etoposide</b>', '<b>Etoposide</b> 160 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 28 days<br> <b>OR</b><br> <b>Etoposide</b> 50 mg PO bid for 14 days<br> Repeat every 21 days', 'Low', 'Johnson DH. Recent developments in chemotherapy treatment of small-cell lung cancer. $Semin Oncol. 1993;20(4):315-325.$', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (104, 13, 'Paclitaxel', '<b>Paclitaxel</b>', '<b>Paclitaxel</b> 250 mg/m<sup>2</sup> IV over 24 hours<br> Repeat every 21 days', 'Low', 'Ettinger DS, Finkelstein DM, Sarma RP, Johnson DH. Phase II study of paclitaxel in patients with extensive-disease small-cell lung cancer: an Eastern Cooperative Oncology Group study. $J Clin Oncol. 1995;13(6):1430-1435.$<br> Kirschling RJ, Grill JP, Marks RS, et al. Paclitaxel and G-CSF in previously untreated patients with extensive-stage small-cell lung cancer: a phase II study of the North Central Cancer Treatment Group. $Am J Clin Oncol. 1999;22(5):517-522$<br>', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (105, 13, 'Topotecan IV', '<b>Topotecan IV</b>', '<b>Topotecan</b> 1.5 mg/m<sup>2</sup>/d IV over 30 minutes, days 1-5<br> Repeat every 21 days', 'Low', 'Ardizzoni A, Hansen H, Dombernowsky P, et al. Topotecan, a new active drug in the second-line treatment of small-cell lung cancer: a phase II study in patients with refractory and sensitive disease. $J Clin Oncol.1997;15(5):2090-2096.$<br> von Pawel J, Schiller JH, Shepherd FA, et al. Topotecan versus cyclophosphamide, doxorubicin, and vincristine for the treatment of recurrent small-cell lung cancer. $J Clin Oncol. 1999;17(2):658-667.$', '<b>Hepatic Impairment</b><ul><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Topotecan : Hycamtin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (106, 13, 'Topotecan PO', '<b>Topotecan PO</b>', '<b>Topotecan</b> 2.3 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 21 days', 'Low', 'Eckardt JR, von Pawel J, Pujol JL, et al. Phase III study of oral compared with intravenous topotecan as second-line therapy in small-cell lung cancer. $J Clin Oncol. 2007;25(15):2086-2092.$ $Erratum in: J Clin Oncol. 2007;25(22):3387.$', '<b>Hepatic Impairment</b><ul><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Topotecan : Hycamtin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (107, 14, 'CAP', '<b>CAP</b>', '<b>Cyclophosphamide</b> 500 mg/m<sub>2</sub> IV on day 1<br><b>Doxorubicin</b> 500 mg/m<sub>2</sub> IV on day 1<br><b>Cisplatin</b> 500 mg/m<sub>2</sub> IV on day 1<br>Repeat cycle every 21 days.', 'High', 'Loehrer PJ, et al.$J Clin Oncol 1994;12:1164-1168.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (108, 14, 'Cisplatin + Etoposide', '<b>Cisplatin + Etoposide</b>', '<b>Cisplatin</b> 60 mg/m<sub>2</sub> IV on day 1<br><b>Etoposide</b> 120 mg/m<sub>2</sub> IV on day 1 - 3<br>Repeat cycle every 21 days.', 'High', 'Giaccone G, et al.$J Clin Oncol 1996;14:814-820.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (109, 14, 'ADOC', '<b>ADOC</b>', '<b>Cisplatin</b> 50 mg/m<sub>2</sub> IV on day 1<br><b>Doxorubicin</b> 40 mg/m<sub>2</sub> IV on day 1<br><b>Vincristine</b> 0.6 mg/m<sub>2</sub> IV on day 3<br><b>Cyclophosphamide</b> 700 mg/m<sub>2</sub> IV on day 4<br>Repeat cycle every 28 days.', 'High', 'Fornasiero A, et al.$Cancer 1991;68:30-33.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (110, 14, 'VIP', '<b>VIP</b>', '<b>Etoposide(VP-I6)</b> 75 mg/m<sub>2</sub> IV on days 1-4<br><b>Ifosfamide</b> 1200 mg/m<sub>2</sub> IV on days 1-4<br><b>Cisplatin</b> 20 mg/m<sub>2</sub> IV on days 1-4<br><b>Mesna</b> 240 mg/m<sub>2</sub> IV before first ifosfamide dose, then 4 and 8 hours later on days 1-4<br>Repeat cycle every 21 days for a total of 4 cycles.G-CSF support at 5 micro grams/kg/day on days 5-15', 'High', 'Loehrer PJ, et al.$Cancer 2001;91:2010-2015.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (111, 14, 'Gemcitabine + Topotecan', '<b>Gemcitabine + Topotecan</b>', '<b>Gemcitabine</b>1000 mg/m<sub>2</sub> IV on days 1,8, and 15.<br><b>Topotecan</b>0.75-1.5 mg/m<sub>2</sub> IV on days 1,8, and 15.<br> Repeat cycle every 21 days', 'Moderate', 'William WN, et al.$Am J Clin Oncol 2009;32:15-19.$', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Gemcitabine : Gemzar, various<br>Topotecan : Hycamtin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (112, 14, 'Carboplatin + Paclitaxel', '<b>Carboplatin + Paclitaxel</b>', '<b>Carboplatin</b> AUC of 6, IV on day 1 <br><b>Paclitaxel</b>225 mg/m<sub>2</sub> IV on day 1<br> Repeat cycle every 21 days', 'Moderate', 'Lemma GL, et al.$ J Clin Oncol 2011;29:2060-2065.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (113, 15, 'AC', '<b>AC</b>', '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles', 'High', 'Fisher B,Brown AM,Dimitor NV,et al.Two months of doxorubicin-cyclophosphamide with and without interval reinduction therapy compared with 6 months of cyclophosphamide,methotrexade,and fluorouracil in positive-node breast cancer patients with tamoxifen-nonresponsive tumors:results from the National Surgical Adjuvant Breast and Bowel Project B-15. $J Clin Oncol. 1990;8(9):1483-1496$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (114, 15, 'AC/Docetaxel', '<b>AC/Docetaxel</b>', '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles<br> <b>FOLLOWED BY</b><b>Docetaxel</b> 100 mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles   ', 'High for AC, low for docetaxel', 'Swain SM,Jeong JH ,Geyer CE,et al. Longer therapy, iatrogenic amenorrhea, and survival in early breast cancer. $N Engl J Med.2010;362(22):2053-2065$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Docetaxel:<br>No dose reduction </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Docetaxel : Taxotere<br>Doxorubicin : Adriamycin, various');
