INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (116, 15, 'AC  T (dose-dense)               ', '<b>AC  T (dose-dense)</b>               ', '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles<br> <b>Filgrastim</b> (prophylactic) given on days 3-10 of   each cycle<br><b>OR</b> <b>PEG-Filgastrim</b> 6 mg SQ on day 2<b>FOLLOWED BY</b><br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> IV over 3 hours every 14 days for 4 cycles<br> <b>Filgrastim</b> (prophylactic) given on days 3-10 of each cycle<b>OR</b> <b>PEG-Filgastrim</b> 6 mg SQ on day 2.                                                                               ', 'High for AC, low for paclitaxel  ', 'Citron ML, berry DA, Cirrincione C,et al. Randomized trial of dose-dense versus conventionally scheduled and sequential versus concurrent combinational chemotherapy as postoperative adjuvant treatment of node-positive primary breast cancer : first report of intergroup trial C9741/cancer and leukemia Group B trial 9741. $J Clin Oncol. 2003;21(8): 1431-1439$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (117, 16, 'AC  TH                                                  ', '<b>AC  TH</b>                                                  ', '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles<br> <b>FOLLOWED BY</b><br> <b>Docetaxel</b> 100 mg/m<sup>2</sup> IV, day1<br> Repeat every 21 days for 4 cycles<br> <b>Trastuzumab</b> 4 mg/kg IV, day 1,<br> <b>FOLLOWED BY</b><br> 2 mg/kg IV weekly during docetaxel,<br> <b>FOLLOWED BY</b><br> 6 mg/kg IV every 21 days for 1 year                                                      ', 'High for AC, low for docetaxel and/or trastuzumab', 'Neyt M, Albrecht J, Cocquyt V.An economic evaluation of herceptinR  in adjuvant setting: the Breast Cancer International Research Group 006 trial. $Ann Oncol. 2006; 17(3):381-390$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Docetaxel:<br>No dose reduction </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Docetaxel : Taxotere<br>Doxorubicin : Adriamycin, various<br>Trastuzumab : Herceptin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (118, 15, 'AT - Docetaxel', '<b>AT - Docetaxel</b>', '<b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV over 15 minutes, day 1<br> <b>FOLLOWED 1 HOUR LATER BY</b><br> <b>Docetaxel</b> 75 mg/m<sup>2</sup> day 1<br> Repeat every 21 days.', 'Moderate', 'Nabholz,JM,Falkson C,Campos D, et al. Docetaxel and doxorubicin comparewd with doxorubicin and cyclophosphamide as first-line chemotherapy for metastatic breast cancer:results of a randomized , multicenter, phase III trial. $J Clin Oncol. 2003;21(6):968-975.$', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Docetaxel : Taxotere<br>Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (119, 15, 'AT - Paclitaxel', '<b>AT - Paclitaxel</b>', '<b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV over, day 1<br> <b>FOLLOWED 24 HOUR LATER BY</b><br> <b>Paclitaxel</b>  220 mg/m<sup>2</sup>,IV over 3 hours ,day 2<br> Repeat every 21 days for up to 8 cycles', 'Moderate', 'Jassem J, Pienkowski T, Pluzanska A.et al. Doxorubicin and paclitaxel versus fluorouracil, doxorubicin, and cyclophosphamide as first-line therapy for women with metastatic breast cancer: final results of a randomized phase III multicenter trial. $J Clin Oncol. 2001;19(6):1707-1715$', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Doxorubicin : Adriamycin, various<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (120, 15, 'A - T - C (dose-dense)', '<b>A - T - C (dose-dense)</b>', '<b>Doxorubicin</b> 60 mg/m<sup>2</sup> IV ,every 14 days for 4 cycles, <b>FOLLOWED BY</b><br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> IV, every 14 days for 4 cycles, <b>FOLLOWED BY</b><br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup> IV, every 14 days for 4 cycles<br> <b>Filgrastim</b> (prophylactic) given on days 3-10 of each cycle<b>OR</b> <b>PEG-Filgastrim</b> 6 mg SQ on day 2.                                                  ', 'Moderate, low for paclitaxel', 'Citron ML, Berry DA, Cirrincione C,et al. Randomized trial of dose-dense versus conventionally scheduled and sequential versus concurrent combination chemotherapy as postoperative adjuvant treatment of node-positive primary breast cancer : first report of intergroup trial C9741/cancer and leukemia Groüp B Trial 9741. $J Clin Oncol. 2003;21(8):1431-1439$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (121, 15, 'Bevacizumab/Paclitaxel', '<b>Bevacizumab/Paclitaxel</b>', '<b>Bevacizumab</b> 10 mg/kg IV, days 1, 15<br> <b>Paclitaxel</b> 90 mg/m<sup>2</sup> IV over 1 hour, days 1, 8, 15<br> Repeat every 28 days', 'Low', 'Miller KD. E2100: a phase III trial of paclitaxel versus paclitaxel/bevacizumab for metastatic breast cancer. $J Clin Oncol. 2003;3(6):421-422$', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Bevacizumab : Avastin<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (122, 15, 'CAF(oral)', '<b>CAF(oral)</b>', '<b>Cyclophosphamide</b> 100mg/m<sup>2</sup> PO, days 1-14<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV ,days 1,8<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup> IV, days 1,8<br> Repeat every 28 days for 6 cycles      ', 'Moderate-High', 'Hutchins LF, Green SJ, Favolin PM, et al. Randomized controlled trial of cyclophosphamide, methotrexate, and fluorouracil versus cyclophosphamide, doxorubicin, and fluorouracil with and without tamoxifen for high risk, node-negative breast cancer:treatment results of Intergroup Protocol INT-0102. $J Clin Oncol. 2005;23(33):8313-8321.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (123, 15, 'FEC', '<b>FEC</b>', '<b>Cyclophosphamide</b> 75 mg/m<sup>2</sup>/d PO, days 1-14<br> <b> Epirubicin</b> 60 mg/m<sup>2</sup>/d IV, days 1,8<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV, days 1,8<br> Repeat every 28 days for 6 cycles<br> <b>OR</b><br> <b>Cyclophosphamide</b> 500 mg/m<sup>2</sup> IV, day 1<br> <b>Epirubicin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for 4 cycles', 'High', 'Levine MN, Bramwell VH, Pritchard KI, et al. Randomized tiral of intensive cyclophosphamide, epirubicin, and fluorouracil chemotherapy compared with cyclophosphamide, methotrexate, and fluorouracil in premenopausal women with node-positive breast cancer. $J Clin Oncol. 1998;16(8):2651:2658.$<br> French Epirubicin Study Group. Epirubicin-based chemotherapy in metastatic breast cancer patients: role of dose intensity and duration of treatment. $J Clin Oncol. 2000;18(7):3115-3124.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Epirubicin : Ellence, various<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (124, 15, 'CMF', '<b>CMF</b>', '<b>Cyclophosphamide </b>100 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Methotrexate</b> 40 mg/m<sup>2</sup>/d IV, days 1,8<br> <b>Fluorouracil</b> 600 mg/m<sup>2</sup>/d IV, days 1, 8<br> Repeat every 28 days for up to 6 cycles<br> <b>OR</b><br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup> IV, day 1<br> <b>Methotrexate</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 600 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 12 courses', 'Moderate for IV, Moderate-High for PO', 'Amadori D, Nanni O, Marangolo M, et al. Disease-free survival advantage of adjuvant cyclophosphamide, methotrexate, and fluorouracil in patients with node-negative, rapdly proliferating breast cancer: a randomized multicenter study. $J Clin Oncol. 2000:18(17):3125-3134.$<br> Bonadonna G, Zambetti M, Valagussa P. Sequential or alternating doxorubicin and CMF regimens in breast cancer with more than three positive nodes: ten-year results.$ JAMA. 1995:273(7):542-547.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (125, 15, 'Gemcitabine/Paclitaxel', '<b>Gemcitabine/Paclitaxel</b>', '<b>Gemcitabine 1,250 mg/m<sup>2</sup> IV over 30-60 minutes, days 1, 8<br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> IV over 3 hours before gemcitabine, day 1<br> Repeat every 21 days', 'Low', 'Albain KS, Nag SM, Calderillo-Ruiz G, et al. Gemcitabine plus paclitaxel versus paclitaxel monotherapy in patients with metastatic breast cancer and prior anthracycline treatment. $J Clin Oncol. 2008;26(24):3950-3957.$', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Gemcitabine : Gemzar, various<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (126, 15, 'EC', '<b>EC</b>', '<b>Epirubicin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 830 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 8 cycles', 'High', 'Piccart MJ, Di Leo A, Beauduin M, et al. Phase III trial comparing two dose levels of epirubicin combined with cyclophosphamide with cyclophosphamide, methotrexate, and fluorouracil in node-positive breast cancer. $J Clin Oncol. 2001;19(12):3103-3110.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cyclophosphamide : Cytoxan<br>Epirubicin : Ellence, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (127, 15, 'Ixabepilone/Capecitabine', '<b>Ixabepilone/Capecitabine</b>', '<b>Ixabepilone</b> 40 mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days for up to 8 cycles', 'Low', 'Thomas ES, Gomez HL, Li RK, et al. Ixabepilone plus capecitabine for metastatic breast cancer progressing after anthracycline and taxane treatment. $J Clin Oncol. 2007;25(33):5210-5217.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Ixabepilone:<br>Monotherapy:  <br>                                                                                                 If AST and ALT < 2.5 upper normal limit, and bilirubin < 1 upper normal limit, use 40 mg/m2<br>                                                                         If AST or ALT < 10 upper normal limit, and bilirubin < 1 upper normal limit, use 32 mg/m2  <br>                                                                                     If AST and ALT < 10 upper normal limit, and bilirubin 1.5-3 upper normal limit, use 20-30 mg/m2 <br>                                                                  If AST or ALT > 10 upper normal limit, or bilirubin > 3 upper normal limit, do not use <br>                                                                                                                                                                                   In combination with capecitabine:  <br>                                                             If AST and ALT < 2.5 upper normal limit, and bilirubin < 1 upper normal limit, use 40 mg/m2 <br>                                                                        If AST or ALT > 2.5 upper normal limit, or bilirubin > 1 upper normal limit, do not use </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br