INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (128, 16, 'Lapatinib/Capecitabine', '<b>Lapatinib/Capecitabine</b>', '<b>Lapatinib</b> 1,250 mg PO 1 hour before breakfast, daily<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days', 'Low', 'Geyer CE, Forster J, Lindquist D, et al. Lapatinib plus capecitabine for HER2-positive advanced breast cancer. $N Engl J Med. 2006;355(26):2733-2743.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Lapatinib:<br>If Child-Pugh C, reduce dose to 750 mg/d </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Lapatinib:<br>Not studied. Only 2% eliminated by kidneys </li></ul>', 'Capecitabine : Xeloda<br>Lapatinib : Tykerb');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (129, 16, 'Lapatinib/Letrozole', '<b>Lapatinib/Letrozole</b>', '<b>Lapatinib</b> 1,500 mg PO, daily<br> <b>Letrozole</b> 2.5 mg PO, daily', 'Low', 'Johnston S, Pippen J Jr, Pivot X, et al. Lapatinib combined with letrozole versus letrozole and placebo as first-line therapy for postmenopausal hormone receptor-positive metastatic breast cancer. $J Clin Oncol. 200927(33):5538-5546.$', '<b>Hepatic Impairment</b><ul><li>Lapatinib:<br>If Child-Pugh C, reduce dose to 750 mg/d </li><li>Letrozole:<br>If Child-Pugh A or B, no dose reduction <br>                                                     If Child-Pugh C, reduce dose by 50% </li></ul><b>Renal Impairment</b><ul><li>Lapatinib:<br>Not studied. Only 2% eliminated by kidneys </li><li>Letrozole:<br>If CrCl > 10 mL/min, no dose reduction </li></ul>', 'Lapatinib : Tykerb<br>Letrozole : Femara');
