INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (170, 18, 'C-P', '<b>C-P</b>', '<b>Cisplatin</b> 50 mg/m2 IV, day 1<br> <b>Paclitaxel</b> 135 mg/m<sup>2</sup> CIVI over 24 hours<br> Repeat every 21 days for 6 cycles', 'High', 'Monk BJ, Sill MW, McMeekin DS, et al. Phase III trial of four cisplatin-containing doublet commbinations in stage IVB recurrent or persistent cervical carcinoma: A Gynecologic Group Study. $J Clin Oncol. 2009;27(28):4649-4655.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                              