INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (171, 18, 'Cisplatin/Topotecan', '<b>Cisplatin/Topotecan</b>', '<b>Cisplatin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Topotecan</b> 0.75 mg/m<sup>2</sup>/d IV, days 1-3<br> Repeat every 21 days', 'High', 'Long HJ, Bundy BN, Grendys EC, et al. Randomized Phase III trial of cisplatin with or without topotecan in carcinoma of the uterine cervix: a Gynecologic Oncology Group study. $J Clin Oncol. 2005;23(21):4626-4633.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Cisplatin : CDDP<br>Topotecan : Hycamtin');
