INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (175, 22, 'Interleukin-2', '<b>Interleukin-2</b>', '<b>Interleukin-2</b><br> <b>High Dose:</b> 600,000-720,000 units/kg IV over 15 minutes, every 8 hours until toxicity or 14 doses; administer 2 courses separated by 5-9 days<br> Repeat 2-course cycle every 6-12 weeks<br> <b>Low Dose:</b> 72,000 units/kg IV bolus every 8 hours up to 14 doses in 5 days. Repeat treatment following 7-10 days of rest<br> Repeat every 8 weeks<br> <b>OR</b><br> 18 million units/m2/d CIVI, days 1-5 and 12-17; repeat cycle on day 35 <b>followed by</b>  9 million units/m2/d CIVI, days 1-5<br> Repeat second cycle every 28 days for 4 cycles', 'Moderate', 'Fyfe G, Fisher RI, Rosenberg SA, Sznol M, Parkinson DR, Louie AC. Results of treatment of 255 patients with metastatic renal cell carcinoma who received high-dose recombinant interleukin-2 therapy. $J Clin Oncol. 1995;13(3):688-696.$<br> Sleijfer DT, Janssen RAJ, Buter J, et al. Phase II study of subcutaneous interleukin-2 in unselected patients with advanced renal cell cancer on an outpatient basis. $J Clin Oncol. 1992;10(7):1119-1123.$<br> Yang JC, Sherry R, Steinberg SM, et al. Randomized study of high-dose and low-dose interleukin-2 in patients with metastatic renal cancer. $J Clin Oncol. 2003;21(16):3127-3132.$', '<b>Hepatic Impairment</b><ul><li>Interleukin-2:<br>Do not use in liver failure</li></ul><b>Renal Impairment</b><ul><li>Interleukin-2:<br>If Cr > 4-4.5 mg/dL, do not use</li></ul>', '');
