INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (180, 21, 'Gemcitabine/Carboplatin', '<b>Gemcitabine/Carboplatin</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes, days 1 and 8<br> <b>Carboplatin</b> AUC of 5 IV over 1 hour, day 1<br> Repeat cycle every 21 days upto 6 cycles', 'Moderate', 'Bamias A, Moulopoulos LA, Koutras A, et al. The combination of gemcitabine and carboplatin as first-line treatment in patients with advanced urothelial carcinoma. $Cancer. 2006;106(2):297-303.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Carboplatin : Paraplatin<br>Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (181, 21, 'Gemcitabine/Cisplatin', '<b>Gemcitabine/Cisplatin</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1,8,15<br> <b>Cisplatin</b> 70mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', 'High', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (182, 21, 'MVAC', '<b>MVAC</b>', '<b>Methotrexate</b> 30 mg/m<sup>2</sup>/d IV, days 1, 15, 22<br> <b>Vinblastine</b> 3 mg/m<sup>2</sup>/d IV, days 2, 15,22<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, day 2<br> <b>Cisplatin</b> 70 mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', 'High', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (183, 25, 'BEP', '<b>BEP</b>', '<b>Bleomycin</b> 30 units IV bolus, weekly for 12 weeks<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV over 30-60 minutes,days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1-5<br> Repeat every 21 days for 4 courses', 'High', 'Nichols CR, Catalano PJ, Crawford ED, et al. Randomized comparison of cisplatin and etoposide and either bleomycin or ifosfamide in treatment of advanced disseminated germ cell tumors: an Eastern Cooperative Oncology Group, jSouthwest Oncology Group, and Cancer and Leukemia Group B Study. $J Clin Oncol. 1998;16(4):1287-1293.$', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Bleomycin : Blenoxane<br>Cisplatin : CDDP<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (184, 25, 'EP', '<b>EP</b>', '<b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat after 21 days', 'High', 'Motzer RJ, Sheinfeld J, Mazumdar M, et al. Etoposide and cisplatin adjuvant therapy for patients with pathologic stage II germ cell tumors. $J Clin Oncol. 1995;13(11):2700-2704.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (185, 25, 'TIP', '<b>TIP</b>', '<b>Paclitaxel</b> 250 mg/m<sup>2</sup>/d CIVI over 24 hours, day 1<br> <b>Ifosfamide</b> 1,500 mg/m<sup>2</sup>/d IV over 1 hour, days 2-5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d IV over 30 minutes, days 2-5<br> <b>Mesna</b> 500 mg/m<sup>2</sup> IV before ifosfamide, and at 4 and 8 hours after ifosfamide daily, days 2-5<br> <b>Peg-Filgrastim</b> 6 mcg SQ, days 6<br> Repeat every 21 days for 4 cycles', 'High', 'Kondagunta GV, Bacik J, Donadio A, et al. Combination of paclitaxel, ifosfamide, and cisplatin is an effective second-line therapy for patients with relapsed testicular germ cell tumors. $J Oncol. 2005;23(27):6549-6555.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                             