INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (186, 25, 'VIP', '<b>VIP</b>', '<b>Vinblastine</b> 0.11 mg/kg/d IV, days 1, 2<br> <b>OR</b><br> <b>Etoposide</b> 75 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>AND</b><br> <b>Infosfamide</b> 1,2000 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 20mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Mesna</b> 400 mg IV 15 minutes prior to ifosfamide, <b>THEN</b> 1,200 mg/d CIVI, days 1-5<br> Repeat every 21 days for 4 cycles', 'High', 'Loehrer PJ, Lauer R, Roth BJ, et al. Salvage therapy in recurrent germ-cell cancer: ifosfamide and cisplatin plus either vinblastine or etoposide. $Ann Intern Med. 1988;109(7):540-546.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Mesna:<br>No dose reduction</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Etoposide : VP-16<br>Mesna : Mesnex, various<br>Vinblastine : Velban');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (187, 23, 'Cabazitaxel', '<b>Cabazitaxel</b>', '<b>Cabazitaxel</b> 25 mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Prednisone</b> 10 mg PO daily<br> Repeat every 21 days', 'Low', 'de Bono JS, Oudard S, Ozguroglu M, et al. Prednisone plus cabazitaxel or mitoxantrone for metastatic castration-resistant prostate cancer progressing after docetaxel treatment: a randomised open-label trial. $Lancet. 2010;376(9747):1147-1154.$', '<b>Hepatic Impairment</b><ul><li>Cabazitaxel:<br></li></ul><b>Renal Impairment</b><ul><li>Cabazitaxel:<br>Caution should be used in patients with severe renal impairment (creatinine clearence of <30 ml/hr)</li></ul>', 'Cabazitaxel : Jevtana<br>Prednisone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (188, 23, 'Docetaxel/Estramustine', '<b>Docetaxel/Estramustine</b>', '<b>Docetaxel</b> 60 mg/m<sup>2</sup> IV, day 2<br><b>Estramustine</b> 280 mg PO tid, days 1-5<br> Repeat every 21 days until progression or to a maximum of 12 cycles ', 'Moderate-High', 'Petrylak DP, Tangen CM, Hussain MA, et al. Docetaxel and estramustine compared with mitoxantrone and prednisone for advanced refractory prostate cancer. $N Engl J Med. 2004;351(15):1513-1520.$', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Estramustine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li></ul>', 'Docetaxel : Taxotere<br>Estramustine : Estracyte');
