INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (205, 26, 'DI', '<b>DI</b>', '<b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV bolus, day 1,<br> <b>FOLLOWED BY</b><br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> CIVI, day 1<br> <b>Mesna</b> 600 mg/m<sup>2</sup> IV bolus, before ifosfamide, FOLLOWED BY 2,500 mg/m<sup>2</sup> CIVI with ifosfamide, and 1,250 mg/m<sup>2</sup> IV over 12 hours following ifosfamide<br> Repeat every 21 days ', 'Moderate', 'Santoro A, Tursz T, Mouridsen H, et al. Doxorubicin versus CYVADIC versus doxorubicin plus ifosfamide in first-line treatment of advanced soft tissue sarcomas: a randomized study of the European Organization for Research and Treatment of Cancer Soft Tissue and Bone Sarcoma Group. $J Clin Oncol. 1995;13(7):1537-1545.$', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Doxorubicin : Adriamycin, various<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various');
