INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (209, 26, 'Doxorubicin', '<b>Doxorubicin</b>', '<b>Doxorubicin</b> 75 mg/m<sup>2</sup> IV<br> Repeat every 21 days', 'Moderate', 'Nielsen OS, Dombernowsky P, Mouridsen H, et al. High-dose epirubicin is not alternative to standard-dose doxorubicin in the treatment of advanced soft tissue sarcomas: a study of the EORTC soft tissue and bone sarcoma group. $Br J Cancer. 1998;78(12):1634-1639.$', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (210, 27, 'Interferon alfa-2b', '<b>Interferon alfa-2b</b>', '<b>Interferon alfa-2b</b>  30 million IU/m<sup>2</sup> IM <b>OR</b> SQ 3 times per week<br>Repeat until disease progression ', 'Moderate', 'Groopman JE, et al. Recombinant alpha-2 interferon therapy for Kaposi''s sarcoma associated with the acquired immunodeficiency syndrome.$Ann Intern Med. 1984 May;100(5):671-6$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
