INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (212, 27, 'Paclitaxel', '<b>Paclitaxel</b>', '<b>Paclitaxel</b>  135 mg/m<sup>2</sup> IV over 3 hours<br> Repeat every 21 days<br><b>OR</b><br> <b>Paclitaxel<sup>a</sup></b> 100 mg/m<sup>2</sup> IV over 3 hours<br>Repeat every 14 days   ', 'Low', 'Parkash S. Gill, et al. Paclitaxel Is Safe and Effective in the Treatment of Advanced AIDS-Related Kaposi''s Sarcoma.$http://jco.ascopubs.org/content/17/6/1876.full$', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                               