INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (215, 28, 'Docetaxel/Carboplatin', '<b>Docetaxel/Carboplatin</b>', '<b>Docetaxel</b> 75 mg/m<sup>2</sup> IV over 30 minutes, day1<br><b>Carboplatin</b> AUC of 5 mg/mL/min IV over 30 minutes, day 1<br>Repeat every 21 days', 'Moderate', 'Pentheroudakis G, Briasoulis E, Kalofonos HP, et al. Docetaxel and carboplatin combination chemotherapy as outpatient palliative therapy in carcinoma of unknown primary: a multicentre Hellenic Cooperative Oncology Group Phase II study. $Acta Oncol. 2008;47(6): 1148-1155.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Carboplatin : Paraplatin<br>Docetaxel : Taxotere');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (216, 28, 'Gemcitabine/Carboplatin/paclitaxel', '<b>Gemcitabine/Carboplatin/paclitaxel</b>', '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup>  1V, days 1,8<br><b>Carboplatin</b> AUC of 5 IV, day 1<br><b>Paclitaxel</b> 200 mg/m2 IV over 1 hour, day 1<br>Repeat cycle every 21 days', 'Moderate', 'Greco FA, Burris HA III, Litchy S, et al. Gemcitabine, carboplatin, and paclitaxel for patients with carcinoma of unknown primary site: a Minnie pearl cancer center research Network study. J Clin Oncol.2002;20(6):1651-1656.                ', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li><li>paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                           