INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (221, 30, 'ABVD', '<b>ABVD</b>', '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 28 days', 'High', 'Harker WG, Kushlan P, Rosenberg SA. Combination chemotherapy for advanced Hodgkin''s disease after failure of MOPP: ABVD and B-CAVe. $Ann Intern Med. 1984;  101(4):440-446$.<br> Longo DL, Glastein E, Duffey PL,et al. Alternating MOPP and ABVD chemotherapy plus mantle-field radiation therapy in patients with massive mediastinal Hodgikin''s disease. $J Clin Oncol. 1997; 15(11): 3338-3346$.     ', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (222, 30, 'BEACOPP (Escalated)', '<b>BEACOPP (Escalated)</b>', '<b>Bleomycin</b> 10 mg/m<sup>2</sup> IV, day8<br> <b>Etoposide</b> 200 mg/m<sup>2</sup> IV, days 1-3<br> <b>Doxorubicin</b> 35 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 8<br> <b>Procarbazine</b> 100 mg/m<sup>2</sup> PO, days 1-7<br> <b>Prednisone</b> 40 mg/m<sup>2</sup> IV, days 1-14<br> Repeat on day 22 for 8 cycles', 'High', 'Diehl V, Franklin J, Pfreundschuh M, et al. Standard and increased-dose BEACOPP chemotherapy compared with COPP-ABVD for advanced Hodgikin''s disease. $N Engl J Med. 2003;348(24):2386-2395$.', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Bleomycin : Blenoxane<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (223, 30, 'Stanford V', '<b>Stanford V</b>', '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Mechlorethamine</b> 6 mg/m<sup>2</sup> IV,day1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup>/d IV, days 8, 22<br> <b>Bleomycin</b> 5 units per m<sup>2</sup> per day  IV, days 8,22<br> <b>Etoposide</b> 60mg/m<sup>2</sup>/d IV, days 15,16<br> <b>Prednisone</b> 40 mg/m<sup>2</sup> PO every other day', 'High', 'Bartlett NL, Rosenberg SA, Hoppe RT, Hancock SL, Horning SJ.Brief chemotherapy, Stanford V, and adjuvant radiotherapy for bulky or advanced-stage Hodgikin''s disease: a Preliminary report. $J Clin Oncol. 1995; 13(5):1080-1088$.', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Mechlorethamine:<br>No dose reduction</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vinblastine:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Bleomycin : Blenoxane<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Vinblastine : Velban<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (224, 30, 'Brentuximab vedotin', '<b>Brentuximab vedotin</b>', '<b>Brentuximab</b> 1.8 mg/kg IV over 30 minutes, day 1<br> Repeat every 21 days', 'Low', 'Adcetris [package insert]. $Bothell, WA: Seattle Genetics;2011$.', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (225, 31, 'BR', '<b>BR</b>', '<b>Rituximab</b> 375mg/m<sup>2</sup> IV, day 1<br> <b>Bendamustine</b> 90 mg/m<sup>2</sup> IV, days 1, 2<br> Repeat every 28 days for 6 cycles', 'Moderate', 'Rummel MK, Niederle N, Mascmeyere G, et al. Bendamustine plus rituximab is superior in respect of progression free survival and CR rate when compared too CHOP plus rituximab as first-line treatment of patients with advanced follicular, indolent, and matle cell lymphomas: Final results of a randomized phase III study of StiL (Study Group of Indolent Lymphomas, Germany). $Presented at :51st American Society of Hematology Annual Meeting; December 5-8, 2009; New Orleans, LA. Abstract 405.$', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Bendamustine : Treanda<br>Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (226, 31, 'CHOP', '<b>CHOP</b>', '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisolone</b> 50 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 21 days', 'Moderate', 'Bezwoda W, Ristogi RB, Erazo Valla A, et al. Long-term results of a multicentre randomised, comparitive phase III trial of CHOP versus CNOP regimens in patients with intermediate-and high-grade non-Hodgikin''s lymphomas. $ Eur J Cancer. 1995;31A(6):903-911$.', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Prednisolone : Flo-pred, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (227, 31, 'R - CHOP', '<b>R - CHOP</b>', '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1 upto a maximum dose of 2mg<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days ', 'Moderate', 'Coiffier B, Leapage E, Briere J, et al. CHOP chemotherapy plus rituximab compared with CHOP alone in elderly patients with diffuse large B-cell lymphoma. $N Engl J Med. 2002;346(4):235-242.$<br> Czuczman MS, Grillo-Lopez AJ, White CA, et al. Treatment of patients with low-grade B-cell lymphoma with the combination of chimeric anti-CD20 monoclonal antibody and CHOP chemotherapy. $J Clin Oncol. 1999; 17(1):268-276$.', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (228, 31, 'CODOX-M/IVAC', '<b>CODOX-M/IVAC</b>', '<u>Cycles 1 AND 3 (CODOX-M)</u><br> <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup>/d IV, days 2-5<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV up to a maximum dose of 2 mg, days 1,8 for cycle 1, days 1,8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 hour, day 10,          <b>THEN</b> 240 mg/m<sup>2</sup>/h CIVI for the next 23 hours<br> <b>Leucovorin calcium </b>192 mg/m<sup>2</sup> IV, starting at hour 36  <b>THEN</b> 12 mg/m<sup>2</sup> every 6 hours until Methotrexate level is <5*10<sup>-8</sup> M<br> <b>Filgrastim</b>5 mcg/kg/d SQ until ANC >1,000/mm<sup>3</sup><br> <b>Methotrexate</b> 12mg IT, day 15<br> <b>Leucovorin calcium</b> 15 mg PO 24 hours after Methotrexate, day 16<br>                                                              <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1,3,5<br> <b>Methotrexate</b> 12 mg IT, day 12 Cycles 2 AND 4 (IVAC)<br> <b>Ifosfamide</b> 1,500mg/m<sup>2</sup> IV over 1 hour, days 1-5<br> <b>Mesna</b> 360 mg/m<sup>2</sup> IV over 1 hour <b>THEN</b> 360 mg/m<sup>2</sup> IV every 3 hours for a maximum of 7 doses  per 24 hour period, days 1-5<br> <b>Cytarabine</b>  2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1 and 2<br> <b>Methotrexate</b> 12 mg IT, day 5<br> <b>Leucovorin calcium</b> 15 mg PO 24 hours after Methotrexate, day 6<br> <b>Filgrastim</b> 5 mcg/kg/d SQ until ANC > 1,000/mm<sup>3</sup>                                                  ', 'Moderate', 'Mead GM, Sydes MR, Walewski J, et al. An international evaluation of CODOX-M and CODOX-M alternating with IVAC in adult Burkitt''s lymphoma: results of United Kingdom Lymphoma Group LY06 Study. $Ann Oncol. 2002, 13(8): 1254-1274.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (229, 31, 'R - CVP', '<b>R - CVP</b>', '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> up to a maximum dose of 2 mg IV, day 1<br> <b>Prednisone</b> 40 mg PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for a maximum off 8 cycles ', 'Moderate', 'Marcus R, Imrie K, Belch A, et al. CVP chemotherapy plus rituximab compared with CVP as first-line treatment for advanced follicular lymphoma. $Blood, 105(4): 1417-1423.$<br> Marcus R, Imrie K, Solal-Celigny P, et al. Phase III study of R-CVP compared with cyclophosphamide, vincristine and prednisone alone in patients with previously utreated advanced follicular lymphoma. $J Clin Oncol. 2008;26(28):4578-4589.$', '<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (230, 31, 'DHAP', '<b>DHAP</b>', '<b>Dexamethasone</b> 40 mg/d PO or IV over 24 hours, days 1-4<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> CIVI, day 1<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 2 doses, day 2<br> Repeat every 21-28 days', 'High', 'Velasquez WS, Cabanillas F, Salvador P, et al. Effective salvage therapy for lymphoma with cisplatin in combination with high-dose Ara-C and dexamethasone (DHAP). $Blood. 1988;71(1):117-122.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Dexamethasone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (231, 31, 'ESHAP', '<b>ESHAP</b>', '<b>Etoposide</b> 40 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2-3 hours,             day 5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>OR</b><br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-4<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2 hours, day 5, immediately following completion of cisplatin<br> Repeat every 21-28 days        ', 'High', 'Velasquez WS, McLaughlin P, Tucker S, et al. ESHAP- an effective chemotherapy regimen in refractory and relapsing lymphoma: a 4-year follow-up study. $J Clin Oncol. 1994; 12(6): 1169-1176.$<br> Rodriguez MA, Cabanillas FC, Velasquez W, et al. Results of a salvage treatment program for relapsing lymphoma: MINE consolidated with ESHAP. $J Clin Oncol. 1995; 13(7): 1734-1741.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (232, 31, 'Hyper-CVAD/MTX-Ara-C', '<b>Hyper-CVAD/MTX-Ara-C</b>', '<u>COURSE 1</u><br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> over 3 hours every 12 hours  for 6 doses , days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> over 48 hours after cyclophosphamide, days 4-5<br> <b>Vincristine</b> 2 mg administered 12 hours  after last cyclophosphamide dose, repeated on day 11<br> <b>Dexamethasone</b> 40 mg/d IV or PO,days 1-4,11-14<br> <b>Filgrastim</b> 5 mcg/kg IV or SQ beginning 24 hours after doxorubicin is complete.<br> <u>COURSE 2 <br>(BEGIN AFTER CLINICAL AND HEMATOLOGIC RECOVERY FROM COURSE 1)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus, day 1<br> <b>FOLLOWED BY</b><br> <b>Methotrexate</b> 800mg/m<sup>2</sup>over 24 hours day1 <b>Followed by</b><br><b>Cytarabine</b>3000 mg/m<sup>2</sup> IV every 12 hours for 4 doses, days 2-3 (dose reduced to 1,000 mg/m<sup>2</sup> in patients >60 years or with serum creatinine >1.5 mg/dL)<br> <b>Leucovorin calcium</b> 50 mg PO 24 hours after end of Methotrexate infusion<br> <b>FOLLOWED 6 HOURS LATER BY</b><br> <b>Leucovorin calcium</b> 15 mg PO every 6 hours for 8 doses (dose adjusted for Methotrexate concentration at 24 and 48 hours after completion of Methotrexate infusion)<br> <b>Filgrastim</b> 5mcg/kg IV or SQ begining 24 hours after cytarabine.<br> Repeat courses every 21 days.            ', 'High', 'Khouri IF, Romaguera J, Kantrajian H,et al. Hyper-CVAD and high -dose methotrexate/ cytarabine followed by stem-cell tans-plantation: an active regimen for aggressive mantle-cell lymphoma. $J Clin Oncol. 1998; 16(12): 3803-3809$.', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (233, 31, 'R-EPOCH', '<b>R-EPOCH</b>', '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 65 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Vincristine</b> 0.5 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 5<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> Repeat every 21 days for 4 -6 cycles', 'Moderate', 'Jermann M, jost M, Taverna C, et al. Rituximab EPOCH, an effective salvage therapy for relapsed, refractory or transformed B-cell lymphomas: results of a phase II study. $Ann Oncol. 2004; 15(3):511-516$.', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (234, 31, 'RICE', '<b>RICE</b>', '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 every 14 days<br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> with mesna 5,000 mg/m<sup>2</sup> CIVI over 24 hours, day 4<br> <b>Carboplatin</b> AUC of 5 (maximum 800 mg) IV, day 4<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 3-5<br> Repeat every 14 days', 'Moderate', 'Kewalramani T, Zelenetz AD, Nimer SD, Nimer SD, et al. Rituximab and ICE as second-line therapy before autologous stem cell transplantation for relapsed or primary refractory diffuse large B-cell lymphoma. $Blood. 2004; 103(10):3684-3688.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (235, 31, 'Bendamustine', '<b>Bendamustine</b>', '<b>Bendamustine</b> 120 mg/m<sup>2</sup> IV days 1 and 2<br> Repeat every 21 days', 'Moderate', 'Kahl BS, Bartlett NL, Leonard JP, et al. Bendamustine is effective therapy in patients with rituximab-refractory, indolent B-cell non-Hodgikin lymphoma: results from a multicenter study. $Cancer. 2010,116(1):106-114 $.', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Bendamustine : Treanda');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (236, 31, 'Bexarotene', '<b>Bexarotene</b>', '<b>Bexarotene</b> 300 mg/m<sup>2</sup>/d PO with a meal', 'Low', 'Targretin [package insert]. $Woodcliff Lake, NJ: Eisai Inc.; 2011.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Bexarotene : Targretin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (237, 31, 'Bortezomib', '<b>Bortezomib</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1,4, 8, 11<br> Repeat every 21 days for up to 17 cycles.', 'Low', 'Goy A, Bernstein SH, Kahl BS, et al. Bortezomib in patients with relapsed or refractory mantle cell lymphoma: updated time to event analyses of the multicenter Phase 2. PINNACLE study. $Ann Oncol. 2009;20(3):520-525.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Bortezomib : Velcade');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (238, 31, 'Brentuximab vedotin', '<b>Brentuximab vedotin</b>', '<b>Brentuximab</b> 1.8 mg/kg IV over 30 minutes,  day 1<br> Repeat every 21 days for upto 16 cycles', 'Low', 'Advani RH, Shustov AR, Brice P, et al.                                     Brentuximab vedotin (SGN-35) in patients with relapsed or refractory systemic anaplastic large cell lymphoma: A Phase 2 study update. $Blood (ASH Annula Meeting Abstracts). 2011; 118:443.$<br> Adcetris [package insert]. $Bothell, WA: Seattle Genetics; 2011.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
