INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (240, 31, 'Fludarabine', '<b>Fludarabine</b>', '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV over 10 minutes, days 1-5<br> Repeat every 28 days for up to 8 courses', 'Low', 'Klasa RJ,Meyer RM, Shustic C, et al. Randomized phase III study of fludarabine phosphate versus cyclophosphamide, vincristine, and prednisione in patients with recurrent low-grade Non-Hodgikin''s lymphoma previously treated with an alkylating agent or alkylatic containing regimen. $J Clin Oncol. 2002;20(24):4649-4654.$', '<b>Hepatic Impairment</b><ul><li>Fludarabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Fludarabine : Fludara');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (241, 31, 'Ibritumomab tiuxetan', '<b>Ibritumomab tiuxetan</b>', '<b>Ibritumomab tiuxetan</b> is given in 2 steps:<br> Step 1: <b>Rituximab</b> 250 mg/m<sup>2</sup> infusion <b>FOLLOWED WITHIN 4 HOURS BY</b> 5.0 mCi (1.6 mg total antibody dose) of <b>In- 111 ibrituximomab tiuxetan</b> administered as a 10-minute IV push<br> Step 2: (7-9 days after step 1): <b>Rituximab</b> 250 mg/m<sup>2</sup> infusion <b>FOLLOWED WITHIN 4 HOURS BY</b><br> <b>Y-90 ibritumomab tiuxetan</b> 0.4 mCi/kg (maximum, 32 mCi) admistered as a 10-minute IV push               ', 'Low', 'Zevalin [package insert]. $San Deigo, CA: IDEC Pharmaceutical Coorporation; 2010.$<br> Wiseman GA, White CA, Sparks RB, et al. Biodistribution and dosimetry results from a phase III prospectively randomized controlled trial of Zevali radiommunotherapy for low-grade, follicular, or transofrmed B-cell non-Hodgikin''s lymphoma. $Crit Rev Oncol Hematol. 2001;39(1-2):181-194.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (242, 31, 'Pralatrexate', '<b>Pralatrexate</b>', '<b>Pralatrexate</b> 30 mg/m<sup>2</sup> IV, weekly for 6 weeks<br> Repeat every 7 weeks', 'Low', 'Folotyn [package insert]. $Westminster, CO: Allos Therapeutics Inc.; 2012.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Pralatrexate : Folotyn');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (243, 31, 'Rituximab Maintenance', '<b>Rituximab Maintenance</b>', '<b>Rituximab</b> 375 mg/m<sup>2</sup>/d IV, day 1<br> Repeat every 90 days until relapse or maximum of 2 years', 'Low', 'van Oers NH, Van Glabbeke M, Glurgea L, et al. Rituximab maintenance treatment of relapsed/residual follicular Non Hodgikin''s lymphoma: Long-term outcome or EROTC 20891 phase III randomized intergroup study. $ JClin Oncol. 2010;28(17):2853-2858.$', '<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (244, 31, 'Romidepsin', '<b>Romidepsin</b>', '<b>Romidepsin</b> 14mg\m<sup>2</sup> IV on days 1,8,and 15.<br> Repeat every 28 days           ', 'Low', 'Istodax [package insert]. $Summit , NJ: Celgene Corporation; 2011.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (245, 31, 'Temsirolimus', '<b>Temsirolimus</b>', '<b>Temsirolimus</b> 175 mg IV per week for 3 weeks<br> <b>FOLLOWED BY</b><br> <b>Temsirolimus</b> 75 mg IV weekly  ', 'Low', 'Hess G, Herbrecht R, Romaguera J, et al. Phase III study to evalute temsirolimus compared with investigator''s choice therapy for the treatment or relapsed or refractory mantle cell lymphoma. $J Clin Oncol.2009; 27(23):3822-3829.$', '<b>Hepatic Impairment</b><ul><li>Temsirolimus:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Temsirolimus:<br>No dose reduction </li></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (246, 31, 'Tositumomab', '<b>Tositumomab</b>', 'Dosimetric step: <b>Tositumomab</b> 450 mg IV over 60 minuutes<br> <b>I-131 tositumomab</b> (35 mg tositumomab containing 5.0 mCi I-131) IV over 20 minutes<br> Therapeutic step: <b>Tositumomab</b> 450mg infused over 60minutes<br> <b>I-131 tositumomab</b> (dose individualized for each patient based on results of dosemetric step)', 'Low', 'Bexxar [package insert]. $Seattle, WA, and Philadelphia, PA: Corixa Corp and GlaxoSmithKline;2011.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (247, 31, 'Vorinostat', '<b>Vorinostat</b>', '<b>Vorinstat</b> 400mg PO daily with food', 'Low', 'Zolinza [package insert]. $Whitehouse station, NJ: Merck & Co., Inc.; 2011.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Vorinostat : Zolinza');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (248, 29, 'CDE', '<b>CDE</b>', '<b>Cyclophosphamide</b> 200mg/m<sup>2</sup>/d CIVI for 4 days<br><b>Doxorubicin</b> 12.5 mg/m<sup>2</sup>/d CIVI for 4 days<br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d CIVI for 4 days<br> Repeat every 28 days for a maximum of 8 cycles', 'Moderate', 'Sparano JA,Lee S, Chen MG, et al. Phase II trial of   infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Group Trial (E1491). $J Clin Oncol. 2004; 22(8): 1491-1500.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (249, 29, 'CHOP', '<b>CHOP</b>', '<b>Cyclophosphamide</b> 750mg/m<sup>2</sup> IV, day 1<br> Doxorubicin 50 mg/m<sup>2</sup> IV, day 1<br><b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 100mg/m<sup>2</sup> PO, days 1-5<br> Repeat every 21 days.', 'Moderate', 'Weiss R, Mitrou P, Arasteh K, et.al. Acquired immunodeficiency syndrome-related lymphoma: Simultaneous treatment with combined cyclophosphamide, doxorubicin, Vincristine, and prednisone chemotherapy and highly active antiretroviral therapy is safe and improves survival--results of the German Multicenter Trial. $Cancer. 2006; 106(7): 1560-1568.$ ', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (250, 29, 'CODOX-M/IVAC', '<b>CODOX-M/IVAC</b>', '<u>CYCLES 1 and 3 (CODOX-M)</u><br>  <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 200 mg/m<sup>2</sup>/d IV, days 2-5<br>  <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 8 for cycle 1,days 1, 8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 h, day 10, <b>THEN</b> 240 mg/m2/h CIVI for the next 23 hours<br> <b>Leucovorin calcium</b> 50 mg IV q6h, begining 36 hours from the start of Methotrexate until Methotrexate level <0.05 mcM<br> <b>Filgrastim</b> beginning 24 h from the start of leucovorin until ANC>1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1, 3<br> <b>Methotrexate<b> 12 mg IT, day 15<br> <u>CYCLES 2 AND 4 (IVAC)</u><br> <b>Ifosofamide</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Mesna</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Etoposide</b> 60 mg/m<sup>2</sup> IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV q 12h, days 1 and 2<br> <b>Filgrastim</b> beginning 24 h after completion of chemotherapy until ANC >1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 15<br> Additional IT Treatment for CNS disease- positive patients:<br> Cycle 1: cytarabine 70 mg IT, days 1,3,5, and Methotrexate 12 mg IT, days 15,17<br> Cycle 3: cytarabine 70 mg IT, days 1,3, and Methotrexate 12 mg IT, day 15                                                                                                                                                               ', 'Moderate', 'Wang ES, Strauss DJ, Teruya-Feldstein J, et al. Intensive chemotherapy with cyclophosphosphamide, doxorucibicin, high-dose methotrecate/ ifosfamide, etoposide, and high-dose cytarabine (CODOX-M/IVAC)for human immunodeficiency virus-associated Burkitt lymphoma. $Cancer. 2003;98(6): 1196-1205.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (251, 29, 'EPOCH Dose-adjusted ', '<b>EPOCH Dose-adjusted </b>', '<b>Etoposide</b> 50 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Vincristine</b> 0.4 mg/m<sup>2</sup>/d /CIVI, days 1-4 (96 hours)<br> <b>Cyclophosphamide</b> <br>Cycle1: <br>375 mg/m<sup>2</sup> IV, day 5, if CD4+ cells >= 100/mm<sup>3</sup>; 187 mg/m<sup>2</sup> IV, day 5, if CD4+ cells<100/mm<sup>3</sup><br> After cycle 1: increase dose by 187 mg if nadir ANC > 500/mvL or platelets <25,000/mcL<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Filgrastim</b> 5 mcg/kg/d SQ, day 6, and until ANC >5,000/mcl<br> Repeat every 21 days', 'Moderate', 'Little RF, Pittaluga S, Grant N, et al. Highly effective treatment of acquired immunodeficiency syndrome-related lymphoma with dose-adjusted EPOCH: impact of antiretroviral therapy suspension and tumor biology. $BLOOD.2003;101(12):4653-4659.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Prednisone : various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (252, 32, 'CALGB 8811', '<b>CALGB 8811</b>', '<u>INDUCTION (COURSE 1:4 WEEKS)</u><br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day1 (800 mg/m<sup>2</sup> for patients >=60 years)<br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup> IV, days 1-3 (30 mg/m<sup>2</sup> for patients>=60 years)<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15, 22<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-21 (days 1-7 for patients >=60 years)<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 5, 8, 11, 15, 18, 22<br> <u>EARLY INTENSIFICATION (COURSE 2:4 WEEKS, REPEAT ONCE)</u><br> <b>Methotrexate</b> 15 mg IT, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 1-4, 8-11<br> <b>Vincristine</b> 2 mg IV, days 15, 22<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 15, 18, 22, 25<br> <u>CNS PROPHYLAXIS AND INTERIM MAINTENANCE (COURSE 3:12 WEEKS)</u><br> <b>Cranial RT</b> 2,400 cGy, days 1-12<br> <b>Methotrexate</b> 15 mg IT, days 1, 8, 15, 22, 29<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-70<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 36, 43, 50, 57, 64<br> <u>LATE INTENSIFICATION (COURSE 4:8 WEEKS)</u><br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, days 1, 8, 15<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15<br> <b>Dexamethasone</b> 10 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 29<br> <b>Thioguanine</b> 60 mg/m<sup>2</sup>/d PO, days 29-42<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 29-32, 36-39<br> <u>MAINTENANCE (COURSE 5:4 WEEKS, FOR 2 YEARS FROM DIAGNOSIS)</u><br> <b>Vincristine</b> 2 mg IV, day 1 of every 4 weeks<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5 of every 4 weeks<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 1, 8, 15, 22<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-28', 'Moderate', 'Larson RA, Dodge RK, Burns CP, et al. A five-drug remission induction regimen with intensive consolidation for adults with acute lymphoblastic leukemia: Cancer and Leukemia Group B study 8811. $Blood. 1995;85(8):2025-2037.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Thioguanine : 6-TG<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (253, 32, 'Hyper-CVAD', '<b>Hyper-CVAD</b>', '<u>ODD CYCLES (1, 3, 5, 7)</u><br>                            <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1-3<br> <b>Mesna</b> 600 mg/m<sup>2</sup>/d CIVI, days 1-3, ending 6 hours after cyclophosphamide completion<br> <b>Vincristine</b> 2 mg IV, days 4, 11<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 4<br> <b>Dexamethasone</b> 40 mg/d, days 1-4, 11-14<br> <b>Filgrastim</b> 5 mcg/kg bid SQ beginning 24 hours after doxorubicin completion <br> <b>OR</b><br><b>PEG-Filgrastim</b>6 mg SQ once 24 hours after doxorubicin completion<br> <u>EVEN CYCLES (2, 4, 6, 8)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV over 2 hours, day 1,<br> <b>FOLLOWED BY</b>  800 mg/m<sup>2</sup> IV over 24 hours<br> <b>Leucovorin calcium</b> 15 mg IV every 6 hours for 8 doses, beginning 24 hours after Methotrexate completion; adjust per Methotrexate level<br> <b>Cytarabine</b> 3 g/m<sup>2</sup> IV over 2 hours every 12 hours for 4 doses, days 2, 3<br> <b>Methylprednisolone</b> 50 mg IV bid, days 1-3<br> <b>Filgrastim</b> 6 mg/kg SQ beginning 24 hours after cytarabine completion<br> <b>OR</b><br><b>PEG-Filgrastim</b>6 mg SQ once 24 hours after doxorubicin completion<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 2 of each cycle<br> <b>Cytarabine</b> 100 mg IT, day 8 of each cycle<br>4-16 cycles based on risk profile', 'High', 'Kantarjian HM, O''Brien S, Smith TL, et al. Results of treatment with Hyper-CVAD, a dose-intensive regimen, in adult acute lymphocytic leukemia. $J Clin Oncol. 2000;18(3):547-561.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Mesna : Mesnex, various<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (254, 32, 'Nelarabine', '<b>Nelarabine</b>', '<b>Nelarabine</b> 1,500 mg/m<sup>2</sup>/d IV, days 1, 3 and 5', 'Low', 'Arranon [package insert]. $Research Triangle Park, NC: GlaxoSmithKline;2011.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (255, 33, 'ATRA/Arsenic', '<b>ATRA/Arsenic</b>', '<b>All-trans-retinoic acid (ATRA)</b> 45 mg/m<sup>2</sup> PO in 2 divided doses daily<br> <b>Arsenic trioxide</b> 0.15 mg/kg/d IV over 1 hour, daily beginning day 10 and continued until <5% blasts and absence of promyelocytes', 'Moderate', 'Estey E, Garcia-Manero G, Ferrajoli A, et al. Use of all-trans-retinoic acid plus arsenic trioxide as an alternative to chemotherapy in untreated acute promyelocytic leukemia. $Blood. 2006;107(9):3469-3473.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (256, 33, 'ATRA/Idarubicin', '<b>ATRA/Idarubicin</b>', '<b>All-trans-retinoic acid</b> (ATRA) 45 mg/m<sup>2</sup> PO, daily<br> <b>Idarubicin</b> 12 mg/m<sup>2</sup> IV, on days 2, 4, 6, 8', 'Moderate', 'Sanz MA, Montesinos P, Vellenga E, et al. Risk-adapted treatment of acute promyelocytic leukemia with all-trans retinoic acid and anthracycline monochemotherapy: long-term outcomeon the LPA99 multicenter study by the PETHEMA Group. $Blood. 2008;112(8):3130-3134.$', '<b>Hepatic Impairment</b><ul><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25% </li></ul>', 'Idarubicin : Idamycin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (257, 33, 'Idarubicin/Cytarabine/Etoposide', '<b>Idarubicin/Cytarabine/Etoposide</b>', '<b>Cytarabine</b> 25 mg/m<sup>2</sup> IV <b>FOLLOWED BY</b> 100 mg/m<sup>2</sup> CIVI, days 1-10<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-5<br> <b>Daunorubicin</b> 50 mg/m<sup>2</sup> IV, days 1, 3, 5<br> <b>OR</b><br> <b>Mitoxantrone</b> 12 mg/m<sup>2</sup> IV, days 1, 3, 5<br> <b>OR</b><br> <b>Idarubicin</b> 10 mg/m<sup>2</sup> IV, days 1, 3, 5', 'Moderate', 'Mandelli F, Vignetti M, Suciu S, et al. Daunorubicin versus mitoxantrone versus idarubicin as induction and consolidation chemotherapy for adults with acute myeloid leukemia: the EORTC and GIMEMA group study AML-10. $J Clin Oncol. 2009;32(10):5397-5403.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use </li><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25% </li><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Cytarabine : Ara-C<br>Etoposide : VP-16<br>Idarubicin : Idamycin<br>Mitoxantrone : Novantrone');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (258, 33, '7 + 3', '<b>7 + 3</b>', '<b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7<br> <b>WITH</b><br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>OR</b><br> <b>Idarubicin</b> 13 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>OR</b><br> <b>Mitoxantrone</b> 12 mg/m<sup>2</sup>/d IV, days 1-3', 'Moderate', 'Presler H, Davis RB, Kirshener J, et al. Comparison of three remission induction regimens and two post-induction strategies for the treatment of acute nonlymphocytic leukemia: a Cancer and Leukemia Group B study. $Blood. 1987;69(5):1441-1449.$<br> Wiernik PH, BAnks PL, Case DC Jr, et al. Cytarabine plus idarubicin or daunorubicin as induction and consolidation therapy for previously untreated patients with acute myeloid leukemia. $Blood. 1992;79(2):313-319.$<br> Wahlin A, Hornsten P, Hedenus M, Malm C. Mitoxantrone and cytarabine versus daunorubicin and cytarabine in previously untreated patients with acute myeloid leukemia. $Cancer Chemother Pharmacol. 1991;28(6):480-483.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use </li><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25% </li><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Cytarabine : Ara-C<br>Idarubicin : Idamycin<br>Mitoxantrone : Novantrone');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (259, 33, '7 + 3 with hi-dose daunorubicin <br>(<60 years)', '<b>7 + 3 with hi-dose daunorubicin <br>(<60 years)</b>', '<b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7<br> <b>WITH</b><br> <b>Daunorubicin</b> 90 mg/m<sup>2</sup>/d IV, days 1-3', 'Moderate', 'Fernandez HF, Sun Z, Pao X, et al. Anthracycline dose intensification in acute myeloid leukemia. $N Engl J Med. 2009;361(13):1249-1259.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li></ul>', 'Cytarabine : Ara-C');
