INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (263, 33, 'HiDAC', '<b>HiDAC</b>', 'For patients <60 years of age:<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup> CIVI over 3 hours, every 12 hours, days 1, 3, 5<br> Repeat every 28 days', 'Moderate', 'Mayer RJ, Davis RB, Schiffer CA, et al. Intensive postremission chemotherapy in adults with acute myeloid deukemia. Cancer and Leukemia Group B. $N Engl J Med. 1994;331(14):896-903.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Cytarabine : Ara-C');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (264, 33, 'Cytarabine', '<b>Cytarabine</b>', 'For patients >=60 years of age:<br> <b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-5<br> Repeat every 28 days for 4 courses', 'Low', 'Stone RM, Berg DT, George SL, et al. Postremission therapy in older patients with de novo acute myeloid leukemia: a randomized trial comparing mitoxantrone and intermediate-dose cytarabine with standard-dose cytarabine. $Blood. 2001;98(3):548-553.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Cytarabine : Ara-C');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (265, 34, 'BR', '<b>BR</b>', '<b>Bendamustine</b> 90 mg/m<sup>2</sup> IV on days 1 and 2<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV for the first cycle and 500 mg/m<sup>2</sup> for subsequent cycles<br> Repeat every 28 days for up to 6 cycles', 'Moderate', 'Fischer K, Cramer P, Stilgenbauer S, et al. Bendamustine combined with rituximab (BR) in first-line therapy of advanced CLL: A multicenter phase II trial of the German CLL Study Group (GCLLSG). $J Clin Oncol. 2011;29(26)3559-3566.$', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Bendamustine : Treanda<br>Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (266, 34, 'FC', '<b>FC</b>', '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-3<br> Repeat every 28 days for 6 cycles', 'Moderate', 'Catovsky D, Richards S, Oscier D, et al. Assesmentof fludarabine plus cyclo-phosphamide for patients with chronic lymphocytic leukemia (the LRF CLL4 trial): a randomized ocntrolled trial. $Lancet. 2007; 370(9583):230-239.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fludarabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Cyclophosphamide : Cytoxan<br>Fludarabine : Fludara');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (267, 34, 'FCR', '<b>FCR</b>', '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 cycle 1<br> <b>FOLLOWED BY</b><br> <b>Rituximab</b> 500 mg/m<sup>2</sup> IV, day 1 cycles 2-6<br> Repeat every 28 days for 6 cycles', 'Moderate', 'Robak T, Dmoszynska A, Solal-Celigny P, et al. Rituximab plus fludarabine and cyclophosphamide prolongs progression free survival compared with fludarabine and cyclophosphamide alone in previously treated chronic lymphocytic leukemia. $J Clin Oncol. 2010;28(10):1756-1765.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fludarabine:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Cyclophosphamide : Cytoxan<br>Fludarabine : Fludara<br>Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (268, 34, 'FR', '<b>FR</b>', '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV over 20-30 minutes, days 1-5<br> Repeat every 28 days for a total of 6 cycles<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV<br> Repeat every 7 days for 4 doses', 'Low', 'Byrd JC, Peterson BL, Morrison VA, et al. Randomized phase 2 study of fludarabine with concurrent versus sequential treatment with rituximab in symptomatic, untreated patients with B-cell chronic lympocytic leukemia: results from Cancer and Leukemia Group B 9712 (CALGB 9712). $Blood. 2003;101(1):6-14$', '<b>Hepatic Impairment</b><ul><li>Fludarabine:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Fludarabine : Fludara<br>Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (269, 34, 'PCR', '<b>PCR</b>', '<b>Pentostatin</b> 2 mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup> IV, day 1<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for 6 cycles', 'Moderate', 'Kay NE, Geyer SM, Call TG, et al. Combination chemoimmunotherapy with pentostatin, cyclophosphamide, and rituximab shows significant clinical activity with low accompanying toxicity in previously untreated B chronic lymphocytic leukemia. $Blood. 2007;109(2):405-411.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Pentostatin:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cyclophosphamide : Cytoxan<br>Pentostatin : Nipent<br>Rituximab : Rituxan');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (270, 34, 'Alemtuzumab', '<b>Alemtuzumab</b>', '<b>Alemtuzumab</b> 30 mg per day IV, 3 times per week, upto a maximum of 23 weeks', 'Low', 'Campath [package insert]. $Wayne, NJ: Bayer HealthCare Pharmaceuticals: 2009.$', '<b>Hepatic Impairment</b><ul><li>Alemtuzumab:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Alemtuzumab:<br>Unknown</li></ul>', 'Alemtuzumab : Campath');
