INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (272, 34, 'Chlorambucil', '<b>Chlorambucil</b>', '<b>Chlorambucil</b> 0.4 mg/kg PO <br> Repeat therapy every 14 days, increase upto  0.8 mg/kg  if  tolerated', 'Low', 'Eichhorst BF, Busch R, Stilgenbauer S, et al. First line therapy with fludarabine compared with chlorambucil does not result in a major benefit for elderly patients with advanced chronic lymphocytic leukemia. $Blood. 2009;114:3382-3391.$', '<b>Hepatic Impairment</b><ul><li>Chlorambucil:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Chlorambucil:<br>No dose reduction</li></ul>', 'Chlorambucil : Leukeran');
