INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (274, 34, 'Ofatumumab', '<b>Ofatumumab</b>', '<b>Ofatumumab</b> 300 mg IV, initial dose,<br> <b>FOLLOWED 1 week later BY</b><br> <b>Ofatumumab</b> 2,000 mg IV, weekly dose for 7 weeks<br> Repeat every 28 days for 4 doses', 'Low', 'Arzerra [package insert]. $Research Triangle Prk, NC: GlaxoSmithKline; 2011.$<br> Wierda WG, Kipps TJ, Mayer J, et al. Ofatumumab as single agent CD20 immunotherapy in fluda-rabine-refractory chronic lymphocytic leukemia. $J Clin Oncol. 2010;28:1749-1755.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Ofatumumab : Arzerra');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (275, 35, 'Dasatinib', '<b>Dasatinib</b>', '<b>Dasatinib</b> 100 mg PO, daily (chronic phase)<br> <b>OR</b><br> <b>Dasatinib</b> 140 mg PO, daily (accelerated or blast phase)', 'Low', 'Kantarijan H, Shah NP, Hochhaus A, et al. Dasatinib versus imatinib in newly diagnosed chronic-phase chronic myeloid deukemia. $N Engl J Med. 2010;362(24):2260-2270.$', '<b>Hepatic Impairment</b><ul><li>Dasatinib:<br>Unknown. Metabolism mainly hepatic </li></ul><b>Renal Impairment</b><ul><li>Dasatinib:<br>Unknown. < 4% excreted by kidney </li></ul>', 'Dasatinib : Sprycel');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (276, 35, 'Imatinib', '<b>Imatinib</b>', '<b>Imatinib mesylate</b> 400 mg PO daily <b>OR</b> 600 mg PO daily(accelarated phase / blast crisis) ', 'Low', 'Druker BJ, et al. Efficacy and safety of a specific inhibitor of the BCR-ABL tyrosine kinase in chronic myeloid leukemia.$N Engl J Med. 2001 Apr 5;344(14):1031-7.$', '<b>Hepatic Impairment</b><ul><li>Imatinib:<br>If bilirubin < 3 upper normal limit, no dose reduction <br>                            If bilirubin > 3 upper normal limit, use with caution </li></ul><b>Renal Impairment</b><ul><li>Imatinib:<br>No dose reduction </li></ul>', 'Imatinib : Gleevec');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (277, 35, 'Nilotinib', '<b>Nilotinib</b>', '<b>Nilotinib</b> 300 mg PO bid  (newly diagnosed)<br> <b>OR</b><br> <b>Nilotinib</b> 400 mg PO bid (resistant or intolerant chronic or accelerated phase)', 'Low', 'Kantarjian HM, et al. Nilotinib versus imatinib for the treatment of patients with newly diagnosed chronic phase, Philadelphia chromosome-positive, chronic myeloid leukaemia: 24-month minimum follow-up of the phase 3 randomised ENESTnd trial.$Lancet Oncol. 2011 Sep;12(9):841-51. doi: 10.1016/S1470-2045(11)70201-7. Epub 2011 Aug 17$', '<b>Hepatic Impairment</b><ul><li>Nilotinib:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Nilotinib:<br>Not tested. Not renally excreted </li></ul>', 'Nilotinib : Tasigna');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (278, 36, 'Cladribine', '<b>Cladribine</b>', '<b>Cladribine:</b>0.09 mg/kg/day IV continuous infusion on days 1 to 7 <br>Administer one cycle', 'Low', 'Saven A, et al. $Blood 1992;79:1111-1120.$', '<b>Hepatic Impairment</b><ul><li>Cladribine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Cladribine : 2-CdA');
