INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (281, 37, 'Bortezomib/Doxil', '<b>Bortezomib/Doxil</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Pegylated liposomal doxorubicin</b> 30 mg/m<sup>2</sup> IV over 1 hour, day 4<br> Repeat every 21 days', 'Low', 'Doxil [package insert]. $Raritan, NJ: Centocor Ortho Biotech Products, LP; 2010.$<br> Orlowski RZ, Nagler A, Sonneveld P, et al. Randomized phase III study of pegylated liposomal doxorubicin plus bortezomib compared with bortezomib alone in relapsed or refractory multiple myeloma: combination therapy improves time to progression. $J clin Oncol. 2007;25(25):3892.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Bortezomib : Velcade');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (282, 37, 'Bortezomib/Doxorubicin/Dexamethasone', '<b>Bortezomib/Doxorubicin/Dexamethasone</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup> IV, bolus days 1-4<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, 17-20', 'Moderate', 'Sonneveld P, van der Holt B, Schmidt-Wolf IGH, et al. First analysis of HOVON-65/GMMG-HD4 randomized phase III trial comparing bortezomib, adriamycin, dexamethasone (PAD) vs VAD as induction treatment prior to high dose melphalan (HDM) in patients with newly diagnosed multiple myeloma (MM). $Blood. 2008;112(11):Abstract 653.$', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Bortezomib : Velcade<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (283, 37, 'Bortezomib/Thalidomide/Dexamethasone (VTD)', '<b>Bortezomib/Thalidomide/Dexamethasone (VTD)</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV bolus, days 1, 4, 8, and 11<br> <b>Thalidomide</b> 100 mg PO daily for 14 days and 200 mg daily thereafter<br> <b>Dexamethasone</b> 40 mg PO daily on days 1, 2, 4, 5, 8, 9, 11, and 12; total of 320 mg per cycle', 'Low', 'Cazo M, Tacchetti P, Patriarca F, et al. Bortezomib with thalidomide plus dexamethasone compared with thalidomide plus dexamethasone as induction therapy before, and consolidation therapy after, double autologous stem-cell transplantation in newly diagnosed multiple myeloma: a randomised phase 3 study. $Lancet. 2010;18;376(9758):2075-2085.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Bortezomib : Velcade<br>Thalidomide : Thalomid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (284, 37, 'CyBorD', '<b>CyBorD</b>', '<b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> PO days 1, 8, 15, 22<br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV on days 1, 4, 8, and 11<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, and 17-20<br> Repeat every 28 days', 'Moderate-High', 'Khan ML, Reeder CB, Kumar SK, et al. A comparison af lenalidomide/dexamethasone versus cyclophosphamide/lenalidomide/dexamethasone versus cyclophosphamide/bortezomib/dexamethansone in newly diagnosed multiple myeloma. $Br J Haematol. 2011 [Epub ahead of print].$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Bortezomib : Velcade<br>Cyclophosphamide : Cytoxan<br>Dexamethasone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (285, 37, 'DT-PACE', '<b>DT-PACE</b>', '<b>Dexamethasone</b> 40 mg PO, days 1-4<br> <b> Thalidomide</b> 400 mg/d PO<br> <b>Cisplatin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Etoposide</b> 40 mg/m<sup>2</sup>/d CIVI, days 1-4', 'High', 'Lee CK, Barlogie B, Munshi N, et al. DTPACE: an effective, novel combination chemotherapy with thalidomide for previously treated patients with myeloma. $J Clin Oncol. 2003;21(14):2732-2739.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Thalidomide : Thalomid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (286, 37, 'Lenalidomide/Dexamethasone', '<b>Lenalidomide/Dexamethasone</b>', '<b>Lenalidomide</b> 25 mg/d PO, days 1-21<br> <b>Dexamethasone</b> 40 mg/d PO, days 1-4, 9-12, 17-20<br> Repeat every 28 days for 4 cycles', 'Low', 'Dimopoulos M, Spencer A, Attal M, et al. Lenalidomide plus dexamethasone for relapsed or refractory multiple myeloma. $N Engl J Med. 2007;357(21):2123-2132.$', '<b>Hepatic Impairment</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency </li></ul>', 'Dexamethasone : various<br>Lenalidomide : Revlimid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (287, 37, 'Melphalan/Prednisone (MP)', '<b>Melphalan/Prednisone (MP)</b>', '<b>Melphalan</b> 8 - 10 mg per m<sup>2</sup>  on days 1 - 4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> days 1 - 4<br> Repeat cycle every 42 days', 'Low', ' $Southwest Oncology Group Study. Arch Intern Med 1975;135:147-152$', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Melphalan : Alkeran<br>Prednisone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (288, 37, 'Melphalan/Prednisone/Talidomide (MPT)', '<b>Melphalan/Prednisone/Talidomide (MPT)</b>', '<b>Melphalan</b> 0.25 mg/kg PO, daily for 5 days<br> <b>Prednisone</b> 1 mg/kg PO, daily for 5 days<br> Repeat every 28 days for 8 cycles<br> <b>Thalidomide 50 - 100 mg PO, daily ', 'Low', ' $Palumbo A, et al. Lancet 2006;28:367-835.$', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Melphalan : Alkeran<br>Prednisone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (289, 37, 'RVD', '<b>RVD</b>', '<b>Lenalidomide</b> 25 mg/d PO, days 1-14<br> <b> Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Dexamethasone</b> 20 mg/d PO days 1, 2, 4, 5, 8, 9, 11, 12 (cycles 1-4)<br> Repeat every 21 days for 8 cycles', 'Low', 'Richardson PG, Weller E, Lonial S, et al. Lenalidomide, bortezomib, and dexamethasone combination therapy in patients with newly diagnosed multiple myeloma. $Blood. 2010;116(5):679-686.$', '<b>Hepatic Impairment</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency </li></ul>', 'Bortezomib : Velcade<br>Dexamethasone : various<br>Lenalidomide : Revlimid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (290, 37, 'VAD', '<b>VAD</b>', '<b>Vincristine</b> 0.4 mg/d CIVI, days 1-4<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Dexamethasone</b> 40 mg/d PO, days 1-4, 9-12, 17-20<br> Repeat every 25 days', 'Moderate', 'Segeren CM, Sonneveld P, Holt V, et al. Vincristine, doxorubicin and dexamethasone (VAD) administered as rapid intravenous infusion for first-line treatment in untreated multiple myeloma. $Br J Haematol. 1999;105(1):127-130.$', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (291, 37, 'VMP', '<b>VMP</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11, 22, 25, 29, 32 cycles 1-4<br> <b>FOLLOWED BY</b><br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 8, 22, 29 cycles 5-9<br> <b>Melphalan</b> 9 mg/m<sup>2</sup> PO, days 1-4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> PO, days 1-4<br> Repeat every 6 weeks for 9 cycles', 'Low', 'San Miguel JF, Schalg R, Khuageva NK, et al. MMY-3002 a phase 3 study comparing bortezomib-melphalan-prednisone (VMP) with melphalan-prednisone (MP) in newly diagnosed multiple myeloma. $Blood. 2007;110(11 pt 1): Abstract 78.$<br> San Miguel JF, Schlag R, Khuageva NK, et al. Bortezomib plus melphalan and prednisone for initial treatment of multiple myeloma. $N Engl J Med. 2008;359(9):906-917.$', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Bortezomib : Velcade<br>Melphalan : Alkeran<br>Prednisone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (292, 37, 'Bortezomib', '<b>Bortezomib</b>', '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1, 4, 8, 11<br> Repeat every 21 days ', 'Low', 'Richardson PG, Barlogie B, Berenson J, et al. A phase 2 study of bortezomib in relapsed, refractory myeloma. $N Engl J Med. 2003;348(26):2609-2617.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Bortezomib : Velcade');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (293, 37, 'Dexamethasone', '<b>Dexamethasone</b>', '<b>Dexamethasone</b> 20 mg/m<sup>2</sup>/d PO, days 1-4, 9-12, 17-20<br> Repeat after 14-day rest period', 'N/A', 'Alexanian R, Dimopoulos MA, Delasalle K, Barlogie B. Primary dexamethasone treatment of multiple myeloma. $Blood. 1992;80(4):887-890.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Dexamethasone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (294, 37, 'Thalidomide', '<b>Thalidomide</b>', '<b>Thalidomide</b> 200 mg/d PO at bedtime (increased dose every 2 weeks for 6 weeks up to a dose of 800 mg/d)', 'Low', 'Singhal S, Mehta J, Desikan R, et al. Antitumor activity of thalidomide in refractory multiple myeloma. $N Engl J Med. 1999;341(21):1565-1571.$ $ Erratum in N Engl J Med. 2000;342(5):364.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Thalidomide : Thalomid');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (295, 37, 'Carfilzomib', '<b>Carfilzomib</b>', '<b>Carfilzomib</b> 20 mg/m<sub>2</sub>, Days 1,2,8,9,15, and 16, followed by a 12 day rest period<br> Repeat cycle every 28 days', 'Low', 'Siegel DS, et al. A phase 2 study of single-agent carfilzomib (PX-171-003-A1) in patients with relapsed and refractory multiple myeloma.$Blood. 2012 Oct 4;120(14):2817-25. doi: 10.1182/blood-2012-05-425934. Epub 2012 Jul 25$', '<b>Hepatic Impairment</b><ul><li>Carfilzomib:<br>The safety, efficacy and pharmacokinetics of Carfilzomib have not been evaluated in patients with baseline hepatic impairment.<br> Patients with the following laboratory values were excluded from the KYPROLIS clinical trials: ALT/AST = 3 × upper limit of normal (ULN) and bilirubin = 2 × ULN</li></ul><b>Renal Impairment</b><ul><li>Carfilzomib:<br>No dose adjustment recommended</li></ul>', 'Carfilzomib : Kyprolis');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (296, 37, 'Pomalidamide', '<b>Pomalidamide</b>', '<b>Pomalidamide</b>4 mg PO daily, from Day 1-21 <br>Repeat cycle every 28 days', 'Low', 'Leleu X, et al. Pomalidomide plus low-dose dexamethasone is active and well tolerated in bortezomib and lenalidomide-refractory multiple myeloma: Intergroupe Francophone du Myélome 2009-02.$Blood. 2013 Mar 14;121(11):1968-75. doi: 10.1182/blood-2012-09-452375. Epub 2013 Jan 14$', '<b>Hepatic Impairment</b><ul><li>Pomalidamide:<br>Avoid POMALYST in patients with serum bilirubin greater than 2.0 mg/dL and AST/ALTgreater than 3.0 x ULN.</li></ul><b>Renal Impairment</b><ul><li>Pomalidamide:<br>Avoid Pomalidamide in patients with a serum creatinine greater than 3.0 mg/dL.</li></ul>', 'Pomalidamide : Pomalyst');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (297, 38, 'CDDP/VP-16', '<b>CDDP/VP-16</b>', '<b>Cisplatin</b> 90 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 150 mg/m<sup>2</sup>/d IV, days 3, 4<br> Repeat every 21 days for 4 courses', 'High', 'Kovnar EH, Kellie SJ, Horowitz ME, et al. Preirradiation cisplatin and etoposide in the treatment of high-risk medulloblastoma and other malignant embryonal tumors of the central nervous system: a phase II study. $J Clin Oncol. 1990;8(2):330-336.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (298, 38, 'COPE or "Baby Brain I"', '<b>COPE or "Baby Brain I"</b>', '<u>CYCLE A</u><br> <b>Vincristine</b> 0.065 mg/kg/d (maximum, 1.5 mg) IV, days 1, 8<br> <b>Cyclophosphamide</b> 65 mg/kg/d IV over 30 minutes, day 1<br> <u>CYCLE B</u><br> <b>Cisplatin</b> 4 mg/kg/d IV over 6 hours, day 1<br> <b>Etoposide</b> 6.5 mg/kg/d IV over 1 hour, days 3, 4<br> Regimens given in alternating 28-day cycles in the sequence AABAAB', 'High', 'Duffner PK, Horowitz ME, Krischer JP, et al. Postoperative chemotherapy and delayed radiation in children less than 3 years of age with malignant brain tumors. $N Engl J Med. 1993;328(24):1725-1731.', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (299, 38, 'Vincristine/Cyclophosphamide/Cisplatin', 'Vincristine/Cyclophosphamide/Cisplatin', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> Repeat every 6 weeks for 8 cycles', 'High', 'Douglas JG, Barker JL, Ellenbogen RG, et al. Concurrent chemotherapy and reduced-dose cranial spinal irradiation followed by conformal posterior fossa tumor bed boost for average-risk medulloblastoma: efficacy and patterns of failure. $Int J Radiat Oncol Biol Phys. 2004;58(4):1161-1164.$<br> National Cancer Institute. Phase III randomized study of adjuvant lomustine/cisplatin/vincristine vs cyclophosphamide/cisplatin/vincristine following craniospinal radiotherapy in children with newly diagnosed, average-risk medulloblastoma. $http://cancer.gov.clinicaltrials.CCG-A9961. Accessed January 27, 2012.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (300, 38, 'Carbo/VP-16/Vincristine', '<b>Carbo/VP-16/Vincristine</b>', '<u>CYCLES 1 AND 3 (3-WEEK CYCLES)</u><br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 7, 14<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-3<br> <b>Carboplatin</b> 500 mg/m<sup>2</sup> IV, days 1, 2<br><br> <u>CYCLES 2 AND 4 (3-WEEK CYCLES)</u><br> <b>Vincristine<sup>d</sup></b> 1.5 mg/m<sup>2</sup> IV, days 1, 7, 14<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-3<br> <b>Cyclophosphamide</b> 1,500 mg/m<sup>2</sup> IV, day 1<br> <b>Mesna</b> 750 mg/m<sup>2</sup> IV, 15 minutes before and 4 and 8 hours after cyclophosphamide', 'High', 'Taylor RE, Bailey CC, Robinson K, et al. Results of a randomized study of preradiation chemotherapy versus radiotherapy alone for nonmetastatic medulloblastoma: the International Society of Paediatric Oncology/United Kingdom Children''s Cancer Study Group PNET-3 study. $J Clin Oncol. 2003;21(8):1581-1591.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Carboplatin : Paraplatin<br>Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (301, 38, 'POC', '<b>POC</b>', '<b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Lomustine</b> 100 mg/m<sup>2</sup> PO, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup>/d IV, days 1, 8, 15', 'Moderate', 'Finlay JL, Bayett JM, Yates AJ, et al. Randomized phase III trial in childhood high-grade astrocytoma comparing vincristine, lomustine, and prednisone with the eight-drugs-in-1-day regimen. $J Clin Oncol. 1995;13(1):112-123.$', '<b>Hepatic Impairment</b><ul><li>Lomustine:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Lomustine:<br>If CrCl < 60 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Lomustine : CCNU<br>Prednisone : various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (302, 38, 'Carboplatin', '<b>Carboplatin</b>', '<b>Carboplatin</b> 560 mg/m<sup>2</sup> IV over 1 hour<br> Repeat every 28 days', 'High', 'Friedman HS, Krischer JP, Burger P, et al. Treatment of children with progressive or recurrent brain tumors with carboplatin or iproplatin: a Pediatric Oncology Group randomized phase II study. $J Clin Oncol. 2000;18(22):3819-3828.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li></ul>', 'Carboplatin : Paraplatin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (303, 39, 'A<sub>1</sub>', '<b>A<sub>1</sub></b>', '<b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 3<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 90 mg/m<sup>2</sup> IV, day 5', 'High', 'Kaneko M, Tsuchida Y, Mugishima H, et al. Intensified chemotherapy increases the survival rates in patients with stage 4 neuroblastoma with MYCN amplification. $J Pediatr Hematol Oncol. 2002;24(8):613-621.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (304, 39, 'A<sub>3</sub>', '<b>A<sub>3</sub></b>', '<b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup>/d IV, days 1, 2<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 3<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-5', 'High', 'Kaneko M, Tsuchida Y, Mugishima H, et al. Intensified chemotherapy increases the survival rates in patients with stage 4 neuroblastoma with MYCN amplification. $J Pediatr Hematol Oncol. 2002;24(8):613-621.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (305, 39, 'CAV + P/VP', '<b>CAV + P/VP</b>', '<u>CAV (COURSES 1, 2, 4, 6)</u><br> <b>Cyclophosphamide</b> 70 mg/kg/d IV over 6 hours, days 1, 2<br> <b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d CIVI for 72 hours<br><b>Vincristine</b>0.033mg/kg/d CIVI for 72 hours<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV bolus, day 9<br> <br><u>P/VP (COURSES 3, 5, 7)</u><br> <b>Etoposide</b> 200 mg/m<sup>2</sup>/d IV over 2 hours, days 1-3<br> <b>Cisplatin</b> 50 mg/m<sup>2</sup>/d IV over 1 hour, days 1-4', 'High', 'Kushner BH, LaQuaglia MP, Bonilla MA, et al. Highly effective induction therapy for stage 4 neuroblastoma in children over 1 year of age. $J Clin Oncol. 1994;12(12):2607-2613.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (306, 39, 'High-risk neuroblastoma', '<b>High-risk neuroblastoma</b>', '<b>Cisplatin</b> 60 mg/m<sup>2</sup> IV over 6 hours, day 0<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, day 2<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 2, 5<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup>/d IV, days 3, 4<br> Repeat every 28 days for 5 cycles', 'High', 'Matthay KK, Villablanca JG, Seeger RC, et al. Treatment of high-risk neuroblastoma with intensive chemotherapy, radiotherapy, autologous bone marrow transplantation, and 13-cisretinoic acid. $N Engl J Med. 1999;341(16):1165-1173.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<b