INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (307, 40, 'Chemoreduction', '<b>Chemoreduction</b>', '<b>Vincristine</b> 0.05 mg/kg (for age <12 months)<br> <b>OR</b> 1.5-2 mg/m<sup>2</sup> (for age >12 months)<br> <b>Carboplatin</b> 20 mg/kg (for age <12 months)<br> <b>OR 550-600 mg/m<sup>2</sup> (for age >12 months)<br> <b>Etoposide phosphate</b> 5 mg/kg (for age <12 months) or 150 mg/m2 (for age >12 months)', 'High', 'Sussman DA, Escalona-Benz E, Benz MS, et al. Comparison of retinoblastoma reduction for chemotherapy vs external beam radiotherapy. $Arch Ophthalmol. 2003;121(7):979-984.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (308, 41, 'CD', '<b>CD</b>', '<b>Cisplatin</b> 80 mg/m<sup>2</sup> CI over 24 hours (>1 year of age) <b>OR</b> 3 mg/kg (<1 year of age) IV over 6 hours with post-hydration fluid<br> <br>Doxorubicin</b> 30 mg/m<sup>2</sup>/d CIVI over 48 hours, days 2, 3<br> Administer 4 cycles before surgery, 2 cycles after surgery', 'High', 'Pritchard J, Brown J, Shafford E, et al. Cisplatin, doxorubicin, and delayed surgery for childhood hepatoblastoma: a successful approach- results of the first prospective study of the International Society of Pediatric Oncology. $J Clin Oncol. 2000;18(22):3819-3828.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (309, 42, 'VAD', '<b>VAD</b>', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV weekly for 10 weeks, then every 3 weeks for 15 weeks<br> <b>Dactinomycin</b> 1.5 mg/m<sup>2</sup> IV every 6 weeks for 26 weeks<br> <b>ALTERNATING WITH</b><br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV every 6 weeks for 1 year (stage III)<br> <b>OR</b><br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, day 1<br> <b>Dactinomycin</b> 15 mcg/kg/d IV, days 1-5 or 60 mcg/kg IV, day 1<br> Repeat every 6 weeks; if stage III or IV, add:<br> <b>Doxorubicin</b> 20 mg/m<sup>2</sup> IV, day 1, and RT', 'Vincristine plus dactinomycin, high; vincristine plus doxorubicin, or doxorubicin-containing, moderate', 'Pritchard J, Imeson J , Barnes J, et al. Results of the United Kingdom Children''s Cancer Study Group first Wilms'' tumor study. $J Clin Oncol. 1995; 13(1):124-133.$<br>  de Camargo B, Franco EL. A randomized clinical trial of single-dose versus fractionated-dose dactinomycin in the treatment of Wilms'' tumor. $Cancer. 1994;73(12):3081-3086.$', '<b>Hepatic Impairment</b><ul><li>Dactinomycin:<br>If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Dactinomycin : Actinomycin<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (310, 43, 'IFoVP', '<b>IFoVP</b>', '<b>Ifosfamide</b> 1,800 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV over 1 hour, days 1-5<br> <b>Mesna</b> 2,880 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat every 21 days', 'High', 'Miser JS, Kinsella TJ, Triche TJ, et al. Ifosfamide with mesna uroprotection and etoposide: an effective regimen in the treatment of recurrent sarcomas and other tumors of children and young adults. $J Clin Oncol. 1987;5(8):1191-1198.$', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (311, 43, 'MTX-CDDP/Adr', '<b>MTX-CDDP/Adr</b>', '<b>Methotrexate</b> 12,000 mg/m<sup>2</sup> IV weekly for 2 weeks; escalate dose by 1,000 mg/m<sup>2</sup> in subsequent courses until peak level of 10-8 M is reached (reduce dose to 8,000 mg/m<sup>2</sup> IV for patients >12 years)<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup> IV every 3 hours for 8 doses beginning 16 hours after completion of methotrexate, <b>THEN</b> every 6 hours PO for 8 doses<br> <b>ALTERNATING WITH</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 15 of cycles 1-7,<br> <b>THEN</b> 120 mg/m<sup>2</sup> for cycles 8-10<br> <b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 15-17 of cycles 1-7', 'High', 'Weiner MA, Harris MB, Lewis M, et al. Neoadjuvant high-dose methotrexate, cisplatin, and doxorubicin for the management of patients with nonmetastatic osteosarcoma. $Cancer Treat Rep. 1986;70(12):1431-1432.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li></ul>', 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (312, 43, 'VP/high-dose Ifos', '<b>VP/high-dose Ifos</b>', '<b>Etoposide</b> 100 mg/m<sup>2</sup> days 1-5<br> <b>Ifosfamide</b> 3,500 mg/m<sup>2</sup> days 1-5<br> <b>Mesna</b> 700 mg/m<sup>2</sup> over 4 hours, <b>THEN</b> mesna 700 mg/m<sup>2</sup> at hour 4, 7, 10, 13, days 1-5<br> <b>GCF</b> 5 mcg/kg/d begin day 6<br> Repeat in 3 weeks for a total of 2 courses', 'High', 'Goorin AM, Harris MB, Bernstein M, et al. Phase II/III trial of etoposide and high-dose ifosfamide in newly diagnosed metastatic osteosarcoma: a pediatric oncology group trial. $J Clin Oncol. 2002;20(2):426-433.', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (313, 44, 'ICE', '<b>ICE</b>', '<b>Ifosfamide</b> 1,800 mg/m<sup>2</sup>/d IV over 1 hour, days 0-4<br> <b>Carboplatin</b> 400 mg/m<sup>2</sup>/d IV over 1 hour, days 0, 1<br> <b>Etoposide 100 mg/m<sup>2</sup>/d IV over 1 hour, days 0-4', 'High', 'Cairo MS. The use of ifosfamide, carboplatin, and etoposide in children with solid tumors. $Semin Oncol. 1995;22(3 suppl 7):23-27.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (314, 44, 'VCR/Cyclophosphamide', '<b>VCR/Cyclophosphamide</b>', '<b>Vincristine</b> 2 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 75 mg/m<sup>2</sup> IV bolus, day 1<br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days', 'High', 'Grier HE, Krailo MD, Tarbell NJ, et al. Addition of ifosfamide and etoposide to standard chemotherapy for Ewing''s sarcoma and primitive neuroectodermal tumor of bone. $N Engl J Med. 2003;348(8):694-701.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
