INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (316, 44, 'Topo/CTX', '<b>Topo/CTX</b>', '<b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>FOLLOWED BY</b><br> <b>Topotecan</b> 0.6-0.75 mg/m<sup>2</sup>/d IV as an escalating dose, days 1-5', 'High', 'Saylors RL, Stewart CF, Zamboni WC, et al. Phase I study of topotecan in combination with cyclophosphamide in pediatric patients with malignant solid tumors: a Pediatric Oncolgy Group study. $J Clin Oncol. 1998;16(3):945-952.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Cyclophosphamide : Cytoxan<br>Topotecan : Hycamtin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (317, 44, 'VAC', '<b>VAC</b>', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV weekly for 3 weeks<br> <b>Dactinomycin</b> 0.015 mg/kg/d (maximum, 0.5 mg/kg/d) IV for 5 days<br> <b>Cyclophosphamide</b> 2,200 mg/m<sup>2</sup> IV, day 1', 'High', 'Crist WM, Anderson JR, Meza JL, et al. Intergroup rhabdomyosarcoma study-IV: results for patients with nonmetastatic disease. $J Clin Oncol. 2001;19(12):3091-3102.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Dactinomycin:<br>If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Dactinomycin : Actinomycin<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (318, 45, 'ABVD', '<b>ABVD</b>', '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 14 days for 12 courses', 'High', 'Fryer CJ, Hutchinson RJ, Krailo M, et al. Efficacy and toxicity of 12 courses of ABVD therapy followed by low-dose regional radiation in advanced Hodgkin''s disease in children: a report from the Children''s Cancer Study Group. $J Clin Oncol. 1990;8(12):1971-1980.$', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (319, 45, 'BEACOPP', '<b>BEACOPP</b>', '<b>Bleomycin</b> 10 units/m<sup>2</sup> IV, day 7<br> <b>Etoposide</b> 200 mg/m<sup>2</sup>/d IV, days 0-2<br> <b>Doxorubicin</b> 35 mg/m<sup>2</sup> IV, day 0<br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 0<br> <b>Vincristine</b> 2 mg/m<sup>2</sup> IV, day 7<br> <b>Procarbazine</b> 100 mg/m<sup>2</sup>/d PO, days 0-6<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 0-13<br> Repeat every 21 days', 'High', 'Kelly KM, Hutchinson RJ, Sposto R, et al. Feasibility of upfront dose-intensive chemotherapy in children with advanced-stage Hodgkin''s lymphoma: preliminary results from the Children''s Cancer Group Study Ccg-59704. $Ann Oncol. 2002;13(suppl 1):107-111.', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Bleomycin : Blenoxane<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (320, 45, 'CHOP', '<b>CHOP</b>', '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup>/d IV, days 1, 22<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup>/d IV, days 1, 22<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV weekly for 6 doses<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO for 28 days', 'Moderate', 'Link MP, Donaldson SS, Berard CW, Shuster JJ, Murphy JB. Results of treatment of childhood localized non-Hodgkin''s lymphoma with combination chemotherapy with or without radiotherapy. $N Engl J Med. 1990;322(17):1169-1174.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (321, 46, 'SNCCL/B-ALL', '<b>SNCCL/B-ALL</b>', '<u>INITIAL PHASE</u><br> <b>Cytarabine</b> 50 mg/m<sup>2</sup>/d (maximum, 50 mg/d) IT, days 1, 2, 3, 11<br> <b>Methotrexate</b> 12 mg/m<sup>2</sup>/d (maximum, 12 mg/d) IT, days 4, 11<br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> IV push every 12 hours for 6 doses, days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 4<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup>/d IV, days 4, 11<br> <b>ALTERNATING WITH</b><br><br> <u>SECONDARY PHASE</u><br> <b>Cytarabine</b> 50 mg/m<sup>2</sup> (maximum, 50 mg) IT, day 1<br> <b>Methotrexate</b> 12 mg/m<sup>2</sup> (maximum, 12 mg) IT, day 1<br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus,<br> <b>THEN</b> 800 mg/m<sup>2</sup> CIVI<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 4 doses, days 2, 3<br> <b>Leucovorin calcium</b> 30 mg/m<sup>2</sup> IV at hour 42, then 3 mg/m<sup>2</sup> IV at hours 54, 66, 78', 'High', 'Bowman WP, Shuster JJ, Cook B, et al. Improved survival for children with B-cell acute lymphoblastic leukemia and stage IV small noncleaved-cell lymphoma: a Pediatric Oncology Group study. $J Clin Oncol. 1996;14(4):1252-1261.$', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (322, 47, 'VCR/Asparaginase/Dex/MTX', '<b>VCR/Asparaginase/Dex/MTX</b>', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 0,7, 14, 21<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> IM, 3 times weekly for 9 doses, starting on days 2-4<br> <b>Dexamethasone</b> 6 mg/m<sup>2</sup>/d PO in 3 equal doses (was substituted for prednisone)<br> <b>Methotrexate</b> 8, 10, or 12 mg IT, age-adjusted, days 0, 14 (also days 7, 21 for patients with CNS disease at diagnosis)<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO', 'Moderate for MTX agent IT, minimal for others', 'Bostrom BC, Sensel MR, Sather HN, et al. Dexamethasone versus prednisone and daily oral versus weekly intravenous mercaptopurine for patients with standard-risk acute lymphoblastic leukemia: a report from the Children''s Cancer Group. $Blood 2003;101(10):3809-3817.$', '<b>Hepatic Impairment</b><ul><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Dexamethasone : various<br>Prednisone : various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (323, 47, 'VCR/Prednisone/Pegaspargase/Cytarabine/MTX', '<b>VCR/Prednisone/Pegaspargase/Cytarabine/MTX</b>', '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 0, 7, 14, 21<br> <b>Prednisone</b> 40 mg/m<sup>2</sup> PO, days 0-28, then 10-day taper<br> <b>Pegaspargase</b> 2,500 units/m<sup>2</sup> IM, day 3<br> <b>Cytarabine</b> 30, 50, or 70 mg IT, age-adjusted, day 0<br> <b>Methotrexate</b> 8, 10, <b>OR</b> 12 mg IT, age-adjusted, days 7, 28', 'Moderate for MTX agent IT, minimal for others', 'Avramis VI, Sencer S, Periclou AP, et al. A randomized comparison of native Escherichia coli asparaginase and polyethylene glycol conjugated asparaginase for treatment of children with newly diagnosed standard-risk acute lymphoblastic leukemia: a Children''s Cancer Group Study. $Blood. 2002;99(6):1986-1994.$ $Erratum in: Blood. 2002; 100(6):1531.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Cytarabine : Ara-C<br>Prednisone : various<br>Vincristine : Oncovin, VCR');
