INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (325, 47, 'OD', '<b>OD</b>', '<b>Dexamethasone</b> 6 mg/m<sup>2</sup>/d PO, days 1-28<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> IM, 3 times weekly for 9 doses<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 0, 7, 14, 21<br> <b>Methotrexate</b> 8-12 mg IT, age-adjusted dosing schedule', 'Moderate', 'Bostrom BC, Sensel MR, Sather HN, et al. Dexamethasone versus prednisone and daily oral versus weekly intravenous mercaptopurine for patients with standard-risk acute lymphoblastic leukemia: a report from the Children''s Cancer Group. $Blood 2003;101(10):3809-3817.$', '<b>Hepatic Impairment</b><ul><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Dexamethasone : various<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (326, 47, 'PVDA', '<b>PVDA</b>', '<b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 1-28<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup>/d IV, days 2, 8, 15, 22<br> <b>Daunorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 2, 8, 15, 22<br> <b>Asparaginase</b> 5,000 units/m<sup>2</sup>/d IM, days 2, 5, 8, 12, 15, 19', 'Moderate', 'Lauer SJ, Camitta BM, Leventhal BG, et al. Intensive alternating drug pairs for treatment of hgh-risk chidhood acute lymphoblastic leukemia. Apediatric Oncology Group pilot study. $Cancer. 1993;71(9):2854-2861.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (327, 47, 'TIT (CNS Prophylaxis)', '<b>TIT (CNS Prophylaxis)</b>', '<u>Regimen <%0B>Age 1y <%0B>Age 2y <%0B>Age>=3y</u><br> <b>Methotrexate</b> <%0B>10 mg <%0B>12.5 mg <%0B>15 mg<br> <b>Cytarabine</b> <%0B>20 mg <%0B>25 mg <%0B>30 mg<br> <b>Hydrocortisone</b> <%0B>10 mg <%0B>12.5 mg <%0B>15 mg<br> <b>Administer</b> IT on day 1, then at weeks 1, 2, 3, 6, 11, 16, 21, 26, 31, then every 12 weeks', 'Moderate', 'Lauer SJ, Camitta BM, Leventhal BG, et al. Intensive alternating drug pairs for treatment of hgh-risk chidhood acute lymphoblastic leukemia. Apediatric Oncology Group pilot study. $Cancer. 1993;71(9):2854-2861.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Cytarabine : Ara-C<br>Hydrocortisone : various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (329, 47, 'IDMTX', '<b>IDMTX</b>', '<u>WEEK 1</u><br> <b>Methotrexate</b> 1,000 mg/m<sup>2</sup> IV over 24 hours<br> <b>FOLLOWED BY</b><br> <b>Mercaptopurine</b> 1,000 mg/m<sup>2</sup> IV over 6 hours<br> <b>THEN</b> (48 hours after the start of Methotrexate)<br> <b>Leucovorin calcium</b> 5 mg/m<sup>2</sup> IV every 6 hours for at least 5 doses<br> <u>WEEK 2</u><br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> IM, day 1<br> <b>Mercaptopurine</b> 50 mg/m<sup>2</sup>/d PO, days 1-7<br> Repeat 2-week cycle times 12', 'Week 1, high; week 2, minimal', 'Mahoney DH, Shuster JJ, Nitschke R, et al. Intensification with intermediate-dose intravenous methotrexate is effectie therapy for children with lower-risk B-precursor acute lymphoblastic leukemia: a Pediatric Oncology Group study. $J Clin Oncol. 2000;18(6):1285-1294.$', '<b>Hepatic Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (330, 47, '6-MP/ MTX', '<b>6-MP/ MTX</b>', '<b>Mercaptopurine</b> 25 mg/m<sup>2</sup>/d PO, days 1-56<br> <b>Methotrexate</b> 5,000 mg/m<sup>2</sup> IV over 24 hours, days 8, 22, 36, 50<br> <b>Methotrexate</b> 12 mg/d IT (adjust dose for children<3 years), days 8, 22, 36, 50', 'Moderate', 'Schrappe M, Reiter A, Ludwig WD, et al. Improved outcome in childhood acute lymphoblastic leukemia despite reduced use of anthracyclines and cranial radiotherapy: results of trial ALL-BFM 90. German-Austrian-Swiss ALL-BFM Study Group. $Blood. 2000;95(11):3310-3322.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (331, 47, 'MTX/6-MP', '<b>MTX/6-MP</b>', '<b>Methotrexate</b> 20 mg/m<sup>2</sup> IM weekly, weeks 25-130<br> <b>Mercaptopurine</b> 50 mg/m<sup>2</sup>/d PO, weeks 25-130', 'Low', 'Camitta B, Leventhal B, Lauer S, et al. Intermediate-dose intravenous methotrexate and mercaptopurine therapy for non-T, non-B acute lymphocytic leukemia of chldhood: a Pediatric Oncology Group study. $J Clin Oncol. 1989;7(10):1539-1544.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (332, 48, 'CA', '<b>CA</b>', '<b>Cytarabine</b> 3,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 4 doses<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> IM 3 hours after last dose of cytarabine<br> Repeat 28 days after initial cycle or after recovery of blood counts, whichever is later', 'high', 'Woods WG, Ruymann FB, Lampkin BC, et al. The role of timing of high-dose cytosine arabinoside intensification and of maintenance therapy in the treatment of children with acute nonlymphocytic leukemia. $Cancer. 2006;66(6):1106-1113.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Cytarabine : Ara-C');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (333, 48, 'DA', '<b>DA</b>', '<b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7', 'Moderate', 'Woods WG, Ruymann FB, Lampkin BC, et al. The role of timing of high-dose cytosine arabinoside intensification and of maintenance therapy in the treatment of children with acute nonlymphocytic leukemia. $Cancer. 2006;66(6):1106-1113.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li></ul>', 'Cytarabine : Ara-C');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (334, 48, 'DAT', '<b>DAT</b>', '<b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cytarabine</b> 100 mg/m<sup>2</sup>/d IV, days 1-7<br> <b>Thioguanine</b> 100 mg/m<sup>2</sup>/d PO, days 1-7', 'Moderate', 'Ravindranath Y, Steuber CP, Krischer J, et al. High-dose cytarabine for intensification of early therapy of childhood acute myeloid leukemia: a Pediatric Oncology Group study. $J Clin Oncol. 1991;9(4):572-580.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li></ul>', 'Cytarabine : Ara-C<br>Thioguanine : 6-TG');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (335, 48, 'DCTER', '<b>DCTER</b>', '<b>Dexamethasone</b> 6 mg/m<sup>2</sup>/d (if age < 3 yeras 0.2mg/kg/d) PO tid, days 0-4<br> <b>Cytarabine</b> 200 mg/m<sup>2</sup>/d (if age < 3 yeras 6.7 mg/kg/d) CIVI, days 0-4<br> <b>Thioguanine</b> 100 mg/m<sup>2</sup>/d (if age < 3 yeras 3.3 mg/kg/d) PO bid, days 0-4<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d (if age < 3 yeras 3.3 mg/kg/d) CIVI, days 0-4<br> <b>Daunorubicin</b> 20 mg/m<sup>2</sup>/d (if age < 3 yeras 0.67 mg/kg/d) CIVI, days 0-4<br> <b>Cytarabine</b> IT (age-based doses)<br> Doses in parentheses used for children <3 years of age', 'Moderate', 'Woods WG, Kobrinsky N, Buckley JD, et al. Timed-sequential induction therapy improves postremission outcome in acute myeloid leukemia: a report from the Children''s Cancer Group. $Blood. 1996;87(12):4979-4989.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Cytarabine : Ara-C<br>Dexamethasone : various<br>Etoposide : VP-16<br>Thioguanine : 6-TG');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (336, 48, 'HI-C DAZE', '<b>HI-C DAZE</b>', '<b>Daunorubicin</b> 30 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup>/d IV every 12 hours, days 1-4<br> <b>Etoposide</b> 200 mg/m<sup>2</sup>/d IV, days 1-3, 6-8<br> <b>Azacitidine</b> 150 mg/m<sup>2</sup>/d IV, days 3-5, 8-10', 'High', 'Hurwitz CA, Mounce KG, Grier HE. Treatment of patients with acute myelogenous leukemia: review of clinical trials of the past decade. $J Pediatr Hematol Oncol. 1995;17(3):185-197.$', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Azacitidine : Vidaza<br>Cytarabine : Ara-C<br>Etoposide : VP-16');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (337, 48, 'MACE/consolidation', '<b>MACE/consolidation</b>', '<b>Amsacrine</b> 100 mg/m<sup>2</sup> IV (1-hour infusion), days 1-5<br> <b>Cytarabine</b> 200 mg/m<sup>2</sup> CIVI, days 1-5<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV (1-hour infusion), days 1-5', 'Low', 'Stevens RF, Hann IM, Wheatley K, Gray RG. Marked improvements in outcome with chemotherapy alone in paediatric acute myeloid leukaemia: results of the United Kingdom Medical Research Council''s 10th AML trial. $Br J Haematol. 1998;101(1):130-140.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (338, 48, 'MidAC', '<b>MidAC</b>', '<b>Mitoxantrone</b> 10 mg/m<sup>2</sup> IV (short infusion), days 1-5<br> <b>Cytarabine</b> 1,000 mg/m<sup>2</sup> IV (2-hour infusion), every 12 hours, days 1-3', 'Moderate', 'Stevens RF, Hann IM, Wheatley K, Gray RG. Marked improvements in outcome with chemotherapy alone in paediatric acute myeloid leukaemia: results of the United Kingdom Medical Research Council''s 10th AML trial. $Br J Haematol. 1998;101(1):130-140.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
